import React, { useState, useRef, useEffect } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { Helmet } from 'react-helmet';
import { motion } from 'framer-motion';
import { FiVolume2, FiVolumeX } from 'react-icons/fi';
import YouTube from 'react-youtube';

const GlobalStyle = createGlobalStyle`
  body {
    overflow-x: hidden !important;
    width: 100%;
    position: relative;
    max-width: 100vw;
    box-sizing: border-box;
    
    @media (max-width: 768px) {
      overflow-x: hidden !important;
      width: 100vw;
      max-width: 100vw;
    }
  }

  * {
    box-sizing: border-box;
    max-width: 100%;
  }

  html {
    overflow-x: hidden !important;
    max-width: 100vw;
  }
`;

const PostContainer = styled.div`
  max-width: 800px;
  width: 100%;
  margin: 120px auto 0;
  padding: 20px;
  color: #666;
  font-size: 15px;
  line-height: 1.6;
  
  @media (max-width: 768px) {
    margin: 100px auto 0;
    padding: 12px;
  }

  h1 {
    font-size: 28px;
    color: #1B3358;
    margin-bottom: 32px;
    margin-top: 0;
    line-height: 1.3;
    text-align: center;
    padding-top: 20px;
    
    @media (max-width: 768px) {
      font-size: 22px;
      margin-bottom: 24px;
      padding-top: 10px;
    }
  }

  p {
    margin-bottom: 16px;
  }

  ul {
    margin-bottom: 16px;
    padding-left: 20px;
    
    li {
      margin-bottom: 8px;
    }
  }

  blockquote {
    margin: 20px 0;
    padding: 15px 20px;
    border-left: 4px solid #C09553;
    background: #f8f8f8;
    font-style: italic;
    
    @media (max-width: 768px) {
      margin: 12px 0;
      padding: 10px 15px;
    }
  }
`;

const AudioContainer = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  z-index: 1000;
  max-width: calc(100vw - 40px);
  overflow: hidden;
  
  @media (max-width: 768px) {
    bottom: 12px;
    right: 12px;
    max-width: calc(100vw - 24px);
    overflow: hidden;
  }
`;

const MuteButton = styled.button`
  background: #1B3358;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 14px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.2);
  max-width: 100%;
  overflow: hidden;
  box-sizing: border-box;
  
  &:hover {
    background: #2a4a7f;
  }
  
  svg {
    font-size: 18px;
    flex-shrink: 0;
  }
  
  @media (max-width: 768px) {
    padding: 6px 12px;
    font-size: 12px;
    gap: 6px;
    max-width: 100%;
    
    svg {
      font-size: 14px;
    }
  }
`;

// Removido o componente Content - vamos usar os estilos direto no PostContainer

const Signature = styled.div`
  margin-top: 30px;
  font-style: italic;
  color: #888;
`;

const Highlight = styled.div`
  margin: 20px 0;
  padding: 15px;
  background: #f5f5f5;
  border-radius: 8px;
  font-style: italic;
  color: #444;

  @media (max-width: 768px) {
    margin: 16px 0;
    padding: 12px;
  }
`;

const YouTubeContainer = styled.div`
  position: fixed;
  bottom: -9999px;
  right: -9999px;
  opacity: 0;
  pointer-events: none;
  
  iframe {
    width: 1px;
    height: 1px;
  }
`;

const ScrollLimit = styled.div`
  height: 5px;
  width: 100%;
  position: relative;
  margin-top: 10px;
  margin-bottom: 10px;
  
  @media (max-width: 768px) {
    height: 5px;
    margin-top: 8px;
    margin-bottom: 8px;
  }
`;

const CelularPost = () => {
  const [isMuted, setIsMuted] = useState(false);
  const playerRef = useRef(null);

  // BLOQUEAR SCROLL PARA BAIXO DO COPYRIGHT
  useEffect(() => {
    const blockDownScroll = (e) => {
      const footer = document.querySelector('footer');
      if (footer) {
        const copyright = footer.querySelector('p:last-child'); // © 2025 Apenas um Católico
        if (copyright) {
          const copyrightRect = copyright.getBoundingClientRect();
          const copyrightBottom = copyrightRect.bottom;
          
          // Se o copyright está visível na parte inferior da tela
          if (copyrightBottom <= window.innerHeight) {
            // Bloqueia scroll para baixo
            if (e.deltaY > 0 || (e.touches && e.touches[0] && window.lastTouchY && e.touches[0].clientY < window.lastTouchY)) {
              e.preventDefault();
              e.stopPropagation();
              return false;
            }
          }
        }
      }
    };

    const handleTouchStart = (e) => {
      window.lastTouchY = e.touches[0].clientY;
    };

    const handleTouchMove = (e) => {
      blockDownScroll(e);
      window.lastTouchY = e.touches[0].clientY;
    };

    // Monitora tentativas de scroll para baixo
    window.addEventListener('wheel', blockDownScroll, { passive: false });
    window.addEventListener('touchstart', handleTouchStart, { passive: false });
    window.addEventListener('touchmove', handleTouchMove, { passive: false });

    return () => {
      window.removeEventListener('wheel', blockDownScroll);
      window.removeEventListener('touchstart', handleTouchStart);
      window.removeEventListener('touchmove', handleTouchMove);
    };
  }, []);

  const opts = {
    height: '1',
    width: '1',
    playerVars: {
      autoplay: 1,
      controls: 0,
      disablekb: 1,
      fs: 0,
      start: 0,
      modestbranding: 1,
      playsinline: 1,
      rel: 0,
      mute: 0,
    },
  };

  const onReady = (event) => {
    playerRef.current = event.target;
    event.target.unMute();
    event.target.setVolume(50);
    event.target.playVideo();
  };

  const onEnd = (event) => {
    event.target.seekTo(0);
    event.target.playVideo();
  };

  const toggleMute = () => {
    if (playerRef.current) {
      if (isMuted) {
        playerRef.current.unMute();
        playerRef.current.setVolume(50);
      } else {
        playerRef.current.mute();
      }
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      <GlobalStyle />
      <PostContainer>
        <Helmet>
          <title>Seu celular está destruindo sua vida - Apenas um Católico</title>
          <meta name="description" content="Uma reflexão sobre como o uso do celular está afetando nossa capacidade de viver o momento presente." />
        </Helmet>

        <h1>Seu celular está destruindo sua vida (e não é do jeito que você pensa)</h1>

        <p>Oi, aqui é o João — o "Apenas um Católico" — e hoje eu queria trocar uma ideia sincera com você sobre algo que parece inofensivo, mas está minando muita coisa boa da nossa vida: o celular.</p>

          <p>Antes de qualquer coisa, não quero vir com aquele papo batido que você já escutou mil vezes. Todo mundo já sabe que o TikTok vicia, que o Instagram prende, que o WhatsApp acelera nossa ansiedade... beleza, isso já tá no automático. Mas e se eu te disser que o problema é mais profundo — e mais perigoso?</p>

          <p>O celular não está só te distraindo. Ele está te impedindo de estar inteiro em alguma coisa. É como se ele tivesse quebrado sua capacidade de dar 100% de você a qualquer atividade.</p>

          <p>Você percebe isso no dia a dia: você não pega um ônibus, você pega o ônibus ouvindo música, respondendo mensagem, vendo reels... tudo ao mesmo tempo. Você não estuda, você estuda com o celular do lado. A cada notificação, sua atenção vai embora. E quando não tem nenhuma, você mesmo abre o celular pra procurar alguma coisa. É automático.</p>

          <blockquote>
            "Faz o que deves, e está no que fazes." — São Josemaría Escrivá.
          </blockquote>

          <p>Parece simples, né? Mas olha que profundo: estar no que fazes. Isso significa estar por inteiro, e hoje em dia, quantas vezes no dia você realmente está 100% presente no que está fazendo?</p>

          <p>Poucas, né? Talvez nenhuma.</p>

          <p>A verdade é que a gente se acostumou a viver fragmentado. Metade aqui, metade em outra aba, metade no Instagram, metade numa conversa. Nunca inteiro. E isso vai matando a profundidade, a concentração, o prazer genuíno de viver uma coisa de verdade.</p>

          <Highlight>
            E sabe o pior? A gente acha normal.
            Acha que carregar o celular pra todo canto é normal. Dormir com ele do lado, acordar e já abrir o WhatsApp. E aí, meu amigo, você já começa o dia perdendo. Porque se a primeira coisa que você faz ao acordar é pegar o celular… você já acordou derrotado.
          </Highlight>

          <p>Olha, não tô dizendo pra você deletar tudo, virar monge, jogar o celular no lixo. Não é isso. Mas a mudança começa com uma coisa só: comece a dar o seu 100% em algo.</p>

          <p>Quer ver um exemplo simples? Se for estudar por 20 minutos, estuda por 20 minutos. Sem abrir outra aba, sem olhar o celular, sem responder ninguém. Só isso. Vinte minutos inteiros seus. Depois responde mensagem, depois vê vídeo, depois relaxa.</p>

          <p>O problema não é o celular. O problema é quando ele vira uma extensão de você — e você perde o controle. Então, comece com atitudes pequenas:</p>

          <ul>
            <li>Não leve o celular pro banheiro.</li>
            <li>Não comece o dia com ele.</li>
            <li>Não responda mensagem no meio do filme.</li>
            <li>Não durma com ele debaixo do travesseiro.</li>
          </ul>

          <p>Deixa o celular ser ferramenta, não coleira. E quando você conseguir fazer isso, quando você conseguir estar 100% numa coisa só… parabéns. Porque nesse momento, você começa a viver de verdade. E viver de verdade não dá tempo pra se prender numa vida falsa de tela e notificação.</p>

          <p>Seja inteiro.<br />
          Faz o que deves. Esteja no que fazes.<br />
          Só por hoje, tenta dar o seu 100%. No estudo, no descanso, na oração, no trabalho. E depois me conta como foi.</p>

          <Signature>
            Com fé,<br />
            João – Apenas um Católico
          </Signature>

          <Highlight>
            Até aqui, se você não percebeu, você usou outras coisas... usou o lo-fi vulgo música de fundo... 
            {isMuted ? "(música mutada)" : "(música tocando)"}
          </Highlight>

          <div style={{ 
            height: '40px', 
            borderBottom: '2px solid #1B3358', 
            marginTop: '30px',
            marginBottom: '20px',
            opacity: 0.3 
          }}></div>

          <p style={{ 
            textAlign: 'center', 
            fontStyle: 'italic', 
            color: '#888',
            fontSize: '14px',
            marginBottom: '20px'
          }}>
            — Fim da reflexão —
          </p>

        <ScrollLimit />

        <YouTubeContainer>
          <YouTube
            videoId="rWFy--tFWmk"
            opts={opts}
            onReady={onReady}
            onEnd={onEnd}
          />
        </YouTubeContainer>

        <AudioContainer>
          <MuteButton onClick={toggleMute}>
            {isMuted ? <FiVolumeX /> : <FiVolume2 />}
          </MuteButton>
        </AudioContainer>
      </PostContainer>
    </>
  );
};

export default CelularPost;
