import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';
import { Card } from '../components/Card';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 40px 20px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 10px;
  }
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }
`;

const LoadingSpinner = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.theme.colors.background};
  border-top: 3px solid ${props => props.theme.colors.secondary};
  border-radius: 50%;
  margin: 20px auto;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    width: 30px;
    height: 30px;
    border-width: 2px;
  }
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: ${props => props.theme.colors.error};
  padding: 20px;
  background: ${props => props.theme.colors.background};
  border-radius: 8px;
  margin: 20px 0;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 15px;
    font-size: 0.9rem;
  }
`;

const LaudesCard = styled(motion.div)`
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  padding: 24px;
  margin-bottom: 24px;
  box-shadow: ${props => props.theme.shadows.light};
  border: 1px solid ${props => props.theme.colors.borderColor};
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    box-shadow: ${props => props.theme.shadows.medium};
    transform: translateY(-2px);
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 16px;
    margin-bottom: 16px;
  }
  h2 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: 16px;
    font-size: 1.5rem;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 1.1rem;
      margin-bottom: 8px;
    }
  }

  h3 {
    color: ${props => props.theme.colors.secondary};
    margin: 24px 0 12px;
    font-size: 1.3rem;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 1rem;
      margin: 12px 0 6px;
    }
  }

  p {
    color: ${props => props.theme.colors.text};
    line-height: 1.8;
    margin-bottom: 16px;
    font-size: 1.1rem;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 0.85rem;
      line-height: 1.5;
      margin-bottom: 8px;
    }
  }

  .rubrica {
    color: ${props => props.theme.colors.textLight};
    font-style: italic;
    font-size: 0.9rem;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 0.75rem;
    }
  }
`;

const DataLaudes = styled.div`
  text-align: center;
  margin-bottom: 24px;
  color: ${props => props.theme.colors.textLight};
  font-size: 1.1rem;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.9rem;
    margin-bottom: 16px;
  }
`;

const Laudes = () => {
  const [laudes, setLaudes] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);

  useEffect(() => {
    const fetchLaudes = async () => {
      try {
        // Simulando dados das Laudes para teste
        const mockLaudes = {
          invitatorium: {
            ant: "Vinde, adoremos o Senhor, nosso Rei e Salvador.",
            psalm: "Salmo 95 (94) - Convite ao louvor divino"
          },
          hymn: "Ó esplendor da glória do Pai, ó luz da luz e fonte de luz, ó dia que iluminas o dia, ó sol que brilhas no meio-dia.",
          psalms: [
            {
              ant: "Cantai ao Senhor um cântico novo, porque Ele fez maravilhas.",
              psalm: "Salmo 63 (62) - Sede de Deus"
            }
          ],
          lectioBrevis: {
            reading: "Irmãos, tudo o que é verdadeiro, tudo o que é nobre, tudo o que é justo, tudo o que é puro, tudo o que é amável, tudo o que é de boa fama, se alguma virtude há e se algum louvor existe, seja isso o que ocupe os vossos pensamentos.",
            reference: "Fl 4, 8"
          },
          benedictus: {
            ant: "Bendito seja o Senhor Deus de Israel, porque visitou e redimiu o seu povo.",
            psalm: "Cântico de Zacarias (Lc 1, 68-79)"
          },
          oratio: "Ó Deus, que fizestes brilhar sobre nós a luz do novo dia, concedei-nos passar este dia sem cair em pecado, para que, ao chegar à tarde, vos possamos louvar com alegria. Por nosso Senhor Jesus Cristo, vosso Filho, na unidade do Espírito Santo. Amém."
        };
        
        setLaudes(mockLaudes);
        setLoading(false);
      } catch (err) {
        setError('Não foi possível carregar as Laudes. Por favor, tente novamente mais tarde.');
        setLoading(false);
      }
    };

    // Simular um pequeno delay de carregamento
    setTimeout(() => {
      fetchLaudes();
    }, 1000);
  }, []);

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Laudes (Oração da Manhã)',
        text: `Reze as Laudes de hoje.`,
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      alert("Compartilhamento não suportado neste navegador.");
    }
  };

  if (loading) {
    return (
      <Container>
        <Title>Laudes</Title>
        <LoadingSpinner
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Title>Laudes</Title>
        <ErrorMessage>{error}</ErrorMessage>
      </Container>
    );
  }

  return (
    <Container>
      <Helmet>
        <title>Laudes - Apenas um Católico</title>
        <meta name="description" content="Acompanhe as Laudes da Liturgia das Horas" />
      </Helmet>

      <Title>Laudes</Title>

      <DataLaudes>
        {new Date().toLocaleDateString('pt-BR', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}
      </DataLaudes>

      {laudes && (
        <>
          {laudes.invitatorium && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <h2>Invitátorio</h2>
              <p>{laudes.invitatorium.ant}</p>
              <p>{laudes.invitatorium.psalm}</p>
            </LaudesCard>
          )}

          {laudes.hymn && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <h2>Hino</h2>
              <p>{laudes.hymn}</p>
            </LaudesCard>
          )}

          {laudes.psalms && laudes.psalms.map((psalm, index) => (
            <LaudesCard
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.3 + (index * 0.1) }}
            >
              <h2>Salmo {index + 1}</h2>
              <p className="rubrica">{psalm.ant}</p>
              <p>{psalm.psalm}</p>
            </LaudesCard>
          ))}

          {laudes.lectioBrevis && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.6 }}
            >
              <h2>Leitura Breve</h2>
              <p>{laudes.lectioBrevis.reading}</p>
              <p className="rubrica">{laudes.lectioBrevis.reference}</p>
            </LaudesCard>
          )}

          {laudes.benedictus && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.7 }}
            >
              <h2>Benedictus</h2>
              <p className="rubrica">{laudes.benedictus.ant}</p>
              <p>{laudes.benedictus.psalm}</p>
            </LaudesCard>
          )}

          {laudes.preces && laudes.preces.length > 0 && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.8 }}
            >
              <h2>Preces</h2>
              {laudes.preces.map((prece, index) => (
                <p key={index}>{prece}</p>
              ))}
            </LaudesCard>
          )}

          {laudes.oratio && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.9 }}
            >
              <h2>Oração Conclusiva</h2>
              <p>{laudes.oratio}</p>
            </LaudesCard>
          )}
        </>
      )}
    </Container>
  );
};

export default Laudes;
