import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import styled, { ThemeProvider } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

// Componentes
import Header from './components/Header';
import Footer from './components/Footer';
import LoadingScreen from './components/LoadingScreen';

// Páginas
import Home from './pages/Home';
import Santos from './pages/Santos';
import SaoFrancisco from './pages/SaoFrancisco';
import Oracao from './pages/Oracao';
import Calendario from './pages/Calendario';
import Blog from './pages/Blog';
import Sobre from './pages/Sobre';
import Laudes from './pages/Laudes';
import LiturgiaDiaria from './pages/LiturgiaDiaria';
import CelularPost from './pages/Blog/CelularPost';

// Estilos e Tema
import GlobalStyle from './styles/GlobalStyles';
import { lightTheme, darkTheme } from './styles/GlobalStyles';
import { ThemeContext } from './context/ThemeContext';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const MainContent = styled(motion.main)`
  flex: 1;
  width: 100%;
  display: flex;
  flex-direction: column;
  padding-top: 0; /* Removendo padding-top que pode estar causando problemas */
`;

const pageVariants = {
  initial: {
    opacity: 0,
    y: 20
  },
  in: {
    opacity: 1,
    y: 0
  },
  out: {
    opacity: 0,
    y: -20
  }
};

const pageTransition = {
  type: 'tween',
  ease: 'anticipate',
  duration: 0.4
};

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    // Simular carregamento inicial
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 500); // Reduzindo ainda mais o tempo

    return () => clearTimeout(timer);
  }, []);

  const toggleTheme = () => {
    setIsDarkMode(prevMode => !prevMode);
  };

  if (isLoading) {
    return (
      <ThemeProvider theme={lightTheme}>
        <GlobalStyle />
        <LoadingScreen />
      </ThemeProvider>
    );
  }

  const currentTheme = isDarkMode ? darkTheme : lightTheme;

  return (
    <ThemeProvider theme={currentTheme}>
      <ThemeContext.Provider value={{ isDarkMode, toggleTheme }}>
        <GlobalStyle />
        <AppContainer>
          <Helmet>
            <title>Apenas um Católico - Evangelização e Devoção</title>
            <meta name="description" content="Site católico dedicado à evangelização e devoção aos santos. Conheça a história dos santos, orações e reflexões espirituais." />
          </Helmet>

          <Header />

          <MainContent
            key={window.location.pathname}
            initial="initial"
            animate="in"
            exit="out"
            variants={pageVariants}
            transition={pageTransition}
          >
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/santos" element={<Santos />} />
              <Route path="/santos/sao-francisco" element={<SaoFrancisco />} />
              <Route path="/oracao" element={<Oracao />} />
              <Route path="/calendario" element={<Calendario />} />
              <Route path="/blog" element={<Blog />} />
              <Route path="/blog/celular" element={<CelularPost />} />
              <Route path="/sobre" element={<Sobre />} />
              <Route path="/laudes" element={<Laudes />} />
              <Route path="/liturgia-diaria" element={<LiturgiaDiaria />} />
            </Routes>
          </MainContent>

          <Footer />
        </AppContainer>
      </ThemeContext.Provider>
    </ThemeProvider>
  );
}

export default App;
