import styled from 'styled-components';
import { FiInstagram, FiMail, FiGithub } from 'react-icons/fi';

const FooterContainer = styled.footer`
  background: ${props => props.theme.colors.footerBg};
  color: ${props => props.theme.colors.white};
  padding: 60px 16px;
  width: 100%;
  box-sizing: border-box;
  overflow: hidden;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(
      to right,
      ${props => props.theme.colors.primary},
      ${props => props.theme.colors.secondary},
      ${props => props.theme.colors.accent}
    );
  }
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 16px;
  }
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 32px;
  text-align: center;
  padding: 0 16px;
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 12px;
    padding: 0;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  justify-content: center;
  gap: 32px;
  margin-bottom: 24px;
  
  a {
    color: ${props => props.theme.colors.white};
    font-size: 28px;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    padding: 12px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.1);
    
    &:hover {
      color: ${props => props.theme.colors.secondary};
      background: rgba(255, 255, 255, 0.2);
      transform: translateY(-3px);
    }
    
    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 18px;
      padding: 8px;
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 16px;
    margin-bottom: 12px;
  }
`;

const FooterText = styled.p`
  font-size: 1.1rem;
  color: ${props => props.theme.colors.white};
  opacity: 0.9;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.8;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.9rem;
    line-height: 1.6;
  }
`;

const FooterLinks = styled.div`
  display: flex;
  gap: 32px;
  margin-top: 24px;

  a {
    color: ${props => props.theme.colors.white};
    text-decoration: none;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -4px;
      left: 0;
      width: 100%;
      height: 2px;
      background: ${props => props.theme.colors.secondary};
      transform: scaleX(0);
      transform-origin: right;
      transition: transform 0.3s ease;
    }

    &:hover {
      color: ${props => props.theme.colors.secondary};

      &::after {
        transform: scaleX(1);
        transform-origin: left;
      }
    }

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 0.9rem;
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 16px;
    flex-direction: column;
  }
`;

const Copyright = styled.p`
  font-size: 0.9rem;
  opacity: 0.8;
  margin: 12px 0 0 0;
  color: ${props => props.theme.colors.white};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.8rem;
    margin-top: 8px;
  }
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <SocialLinks>
          <a href="https://www.instagram.com/joaotdev/" target="_blank" rel="noopener noreferrer" aria-label="Instagram">
            <FiInstagram />
          </a>
          <a href="mailto:joao.carlosdev23@gmail.com" aria-label="Email">
            <FiMail />
          </a>
          <a href="https://github.com/JoaocarlosTeco" target="_blank" rel="noopener noreferrer" aria-label="GitHub">
            <FiGithub />
          </a>
        </SocialLinks>
        <FooterText>
          Apenas um Católico. Todos os direitos reservados.
        </FooterText>
        <FooterLinks>
          <a href="https://www.instagram.com/joaotdev/" target="_blank" rel="noopener noreferrer">
            Instagram
          </a>
          <a href="mailto:joao.carlosdev23@gmail.com">
            Email
          </a>
          <a href="https://github.com/JoaocarlosTeco" target="_blank" rel="noopener noreferrer">
            GitHub
          </a>
        </FooterLinks>
        <Copyright>
          © {new Date().getFullYear()} Apenas um Católico. Todos os direitos reservados.
        </Copyright>
      </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
