import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiArrowRight, FiX, FiHeart, FiBookOpen } from 'react-icons/fi';
import Hero from '../components/Hero';
import useDeviceType from '../components/useDeviceType';
import { desktopStyles, mobileStyles } from '../styles/theme';

const HomeContainer = styled.div`
  min-height: 100vh;
`;

const ContentSection = styled.section`
  padding: ${props => props.isMobile ? 
    `${mobileStyles.spacing.xl} ${mobileStyles.spacing.lg}` : 
    `${desktopStyles.spacing.xxl} ${desktopStyles.spacing.xl}`
  };
  max-width: ${props => props.isMobile ? '100%' : '1200px'};
  margin: 0 auto;
`;

const HeroSection = styled.section`
  position: relative;
  min-height: calc(100vh - 175px);
  margin-top: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  overflow: hidden;
  background-image: url('/Fotos/hero-jesus.png');
  background-size: cover;
  background-position: center;
  background-attachment: fixed;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(27, 51, 88, 0.7);
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    min-height: calc(100vh - 85px);
    background-attachment: scroll;
    padding: 10px;
    align-items: flex-start;
    padding-top: 60px;
  }
`;

const ForegroundJesusImage = styled(motion.img)`
  position: absolute;
  right: 5%;
  bottom: 0;
  height: 95%;
  width: auto;
  object-fit: contain;
  object-position: bottom right;
  z-index: 0;

  @media (max-width: ${({ theme }) => theme.breakpoints.tablet}) {
    display: none;
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 2;
  max-width: 1200px;
  width: 100%;
  text-align: center;
  padding: 0 20px;
  opacity: 0;
  animation: fadeIn 1s ease forwards;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    padding: 0 10px;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const HeroText = styled.div`
  h1 {
    font-size: 64px;
    color: white;
    margin-bottom: 24px;
    line-height: 1.2;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
    font-weight: 700;

    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
      font-size: 28px;
      margin-bottom: 12px;
      padding: 0 ${props => props.theme.spacing.sm};
    }
  }

  p {
    font-size: 24px;
    color: white;
    margin-bottom: 32px;
    line-height: 1.6;
    opacity: 0.9;
    max-width: 800px;
    margin-left: auto;
    margin-right: auto;

    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
      font-size: 14px;
      margin-bottom: 16px;
      line-height: 1.4;
      padding: 0 ${props => props.theme.spacing.sm};
    }
  }
`;

const HeroImage = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;

  img {
    max-width: 100%;
    height: auto;
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);

    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
      max-width: 80%;
      border-radius: 16px;
    }
  }
`;

const Logo = styled.img`
  width: 100%;
  max-width: 400px;
  height: auto;
  margin-bottom: 40px;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    max-width: 280px;
    margin-bottom: 24px;
  }
`;

const Section = styled.section`
  padding: ${props => props.theme.spacing.xxl} 0;
  max-width: 1200px;
  margin: 0 auto;
  padding-left: ${props => props.theme.spacing.lg};
  padding-right: ${props => props.theme.spacing.lg};
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 1s ease forwards;
  animation-delay: ${props => props.delay || '0s'};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: ${props => props.theme.spacing.xl} ${props => props.theme.spacing.md};
  }

  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  font-size: clamp(2rem, 5vw, 3rem);
  color: ${props => props.theme.colors.titleColor};
  position: relative;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.5rem;
    margin-bottom: ${props => props.theme.spacing.lg};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const SectionParagraph = styled(motion.p)`
  text-align: center; 
  font-size: 1.2rem; 
  max-width: 800px; 
  margin: -1.5rem auto 2rem auto;
  color: ${props => props.theme.colors.textLight};
  line-height: 1.8;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1rem;
    line-height: 1.6;
    margin: -1rem auto 1.5rem auto;
    padding: 0 ${props => props.theme.spacing.sm};
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: ${props => props.theme.spacing.xl} 0;
  gap: ${props => props.theme.spacing.md};
  
  .line {
    flex: 1;
    max-width: 200px;
    height: 2px;
    background: ${props => props.theme.colors.secondary};
    opacity: 0.5;
  }
  
  .cross {
    color: ${props => props.theme.colors.secondary};
    font-size: 2rem;
  }
`;

const Versiculo = styled(motion.div)`
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.accent});
  color: ${props => props.theme.colors.white};
  padding: ${props => props.theme.spacing.xl};
  border-radius: 15px;
  text-align: center;
  margin: ${props => props.theme.spacing.xl} 0;
  box-shadow: ${props => props.theme.shadows.medium};
  transform: translateY(0);
  transition: transform ${props => props.theme.transitions.normal};
  
  &:hover {
    transform: translateY(-5px);
  }
  
  p {
    font-size: 1.4rem;
    font-style: italic;
    margin-bottom: ${props => props.theme.spacing.md};
    color: ${props => props.theme.colors.white};
    line-height: 1.8;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 1.1rem;
      line-height: 1.6;
      padding: ${props => props.theme.spacing.sm};
    }
  }
  
  a {
    color: ${props => props.theme.colors.secondary};
    font-weight: 600;
    
    &:hover {
      color: ${props => props.theme.colors.white};
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: ${props => props.theme.spacing.lg};
    margin: ${props => props.theme.spacing.lg} 0;
  }
`;

const SantosGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  margin-top: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.lg};
    margin-top: ${props => props.theme.spacing.lg};
    padding: 0 ${props => props.theme.spacing.sm};
  }
`;

const SantoCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: 15px;
  overflow: hidden;
  box-shadow: ${props => props.theme.shadows.medium};
  transition: all ${props => props.theme.transitions.normal};
  cursor: pointer;
  position: relative;
  
  a {
    text-decoration: none;
    color: inherit;
    display: block;
  }
  
  &:hover {
    box-shadow: ${props => props.theme.shadows.heavy};
    transform: translateY(-5px);

    img {
      transform: scale(1.05);
    }

    &::after {
      opacity: 1;
    }
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      to bottom,
      rgba(27, 51, 88, 0) 0%,
      rgba(27, 51, 88, 0.8) 100%
    );
    opacity: 0;
    transition: opacity ${props => props.theme.transitions.normal};
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    width: 90%;
    margin: 0 auto;
  }
`;

const SantoImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-bottom: 3px solid ${({ theme }) => theme.colors.secondary};
  transition: transform ${props => props.theme.transitions.normal};
`;

const SantoInfo = styled.div`
  padding: ${props => props.theme.spacing.lg};
  text-align: center;
  position: relative;
  z-index: 2;
  background: ${props => props.theme.colors.white};
  
  h3 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: ${props => props.theme.spacing.sm};
    font-size: 1.5rem;
    transition: color ${props => props.theme.transitions.normal};
  }
  
  p {
    color: ${props => props.theme.colors.textLight};
    font-size: 1rem;
    transition: color ${props => props.theme.transitions.normal};
  }
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  margin-top: ${props => props.theme.spacing.xl};
`;

const FeatureCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  padding: ${props => props.theme.spacing.xl};
  border-radius: 15px;
  text-align: center;
  box-shadow: ${props => props.theme.shadows.light};
  transition: all ${props => props.theme.transitions.normal};
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.medium};
  }
  
  .icon {
    font-size: 3rem;
    color: ${props => props.theme.colors.secondary};
    margin-bottom: ${props => props.theme.spacing.lg};
  }
  
  h3 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: ${props => props.theme.spacing.md};
    font-size: 1.5rem;
  }
  
  p {
    color: ${props => props.theme.colors.textLight};
    line-height: 1.8;
    font-size: 1.1rem;
  }
`;

const Home = () => {
  const { isMobile } = useDeviceType();

  const santos = [
    {
      id: 4,
      name: 'São Francisco de Assis',
      image: '/Fotos/São franciso.jpg',
      description: 'Padroeiro dos animais e da ecologia',
      link: '/santos/sao-francisco'
    },
    {
      id: 2,
      name: 'Santa Teresinha',
      image: '/Fotos/Santa terezinha.jpg',
      description: 'Doutora da Igreja e padroeira das missões'
    },
    {
      id: 3,
      name: 'São Padre Pio',
      image: '/Fotos/Padre-Pio.webp',
      description: 'Padre estigmatizado e confessor'
    },
    {
      id: 1,
      name: 'Nossa Senhora',
      image: '/Fotos/Nossa senhora.webp',
      description: 'Mãe de Jesus e nossa intercessora'
    }
  ];

  const features = [
    {
      icon: <FiX />,
      title: 'Orações Diárias',
      description: 'Acesse orações tradicionais e contemporâneas para fortalecer sua fé diariamente.'
    },
    {
      icon: <FiBookOpen />,
      title: 'Histórias dos Santos',
      description: 'Conheça a vida e os milagres dos santos que nos inspiram a seguir Cristo.'
    },
    {
      icon: <FiHeart />,
      title: 'Reflexões Espirituais',
      description: 'Meditações e reflexões para aprofundar sua vida espiritual.'
    }
  ];

  return (
    <HomeContainer>
      <Helmet>
        <title>Início - Apenas um Católico</title>
        <meta name="description" content="Bem-vindo ao site católico dedicado à evangelização e devoção aos santos. Jesus: Nosso Senhor e Salvador." />
      </Helmet>

      <Hero 
        title="Apenas um Católico"
        subtitle="Vivendo e compartilhando a fé católica no mundo moderno"
      />

      <ContentSection isMobile={isMobile}>
        <Section>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            O que a Bíblia diz sobre imagens?
          </SectionTitle>
          <SectionParagraph
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
             As imagens nos ajudam a lembrar das obras de Deus e dos santos que seguiram a Cristo.
          </SectionParagraph>
          <Versiculo
            initial={{ opacity: 0, scale: 0.9 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <p>
              "Faze para ti imagens de querubins..."
            </p>
            <a 
              href="https://www.bibliaonline.com.br/nvi/ex/25/18" 
              target="_blank" 
              rel="noopener noreferrer"
            >
              (Êxodo 25:18)
            </a>
          </Versiculo>

          <Divider>
            <div className="line"></div>
            <span className="cross">✝</span>
            <div className="line"></div>
          </Divider>

          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Por que veneramos os Santos?
          </SectionTitle>

          <SectionParagraph
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Homens e mulheres de fé que trilharam os caminhos de Jesus, os santos deixaram um legado de amor, 
            serviço e dedicação que transcende o tempo e continua a inspirar milhões.
          </SectionParagraph>
        </Section>

        <Section>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            História dos Santos
          </SectionTitle>

          <SantosGrid
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            {santos.map((santo, index) => (
              <SantoCard
                key={santo.id}
                whileHover={{ y: -10 }}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                {santo.link ? (
                  <Link to={santo.link}>
                    <SantoImage src={santo.image} alt={santo.name} />
                    <SantoInfo>
                      <h3>{santo.name}</h3>
                      <p>{santo.description}</p>
                    </SantoInfo>
                  </Link>
                ) : (
                  <>
                    <SantoImage src={santo.image} alt={santo.name} />
                    <SantoInfo>
                      <h3>{santo.name}</h3>
                      <p>{santo.description}</p>
                    </SantoInfo>
                  </>
                )}
              </SantoCard>
            ))}
          </SantosGrid>
        </Section>

        <Section>
          <SectionTitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Recursos Espirituais
          </SectionTitle>

          <FeaturesGrid>
            {features.map((feature, index) => (
              <FeatureCard
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </FeatureCard>
            ))}
          </FeaturesGrid>
        </Section>
      </ContentSection>
    </HomeContainer>
  );
};

export default Home;
