import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 40px 20px;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const LoadingSpinner = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.theme.colors.background};
  border-top: 3px solid ${props => props.theme.colors.secondary};
  border-radius: 50%;
  margin: 20px auto;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: ${props => props.theme.colors.error};
  padding: 20px;
  background: ${props => props.theme.colors.background};
  border-radius: 8px;
  margin: 20px 0;
`;

const LaudesCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: 15px;
  padding: 24px;
  margin-bottom: 24px;
  box-shadow: ${props => props.theme.shadows.light};

  h2 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: 16px;
    font-size: 1.5rem;
  }

  h3 {
    color: ${props => props.theme.colors.secondary};
    margin: 24px 0 12px;
    font-size: 1.3rem;
  }

  p {
    color: ${props => props.theme.colors.text};
    line-height: 1.8;
    margin-bottom: 16px;
    font-size: 1.1rem;
  }

  .rubrica {
    color: ${props => props.theme.colors.textLight};
    font-style: italic;
    font-size: 0.9rem;
  }
`;

const DataLaudes = styled.div`
  text-align: center;
  margin-bottom: 24px;
  color: ${props => props.theme.colors.textLight};
  font-size: 1.1rem;
`;

const Laudes = () => {
  const [laudes, setLaudes] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);

  useEffect(() => {
    const fetchLaudes = async () => {
      try {
        const today = new Date();
        const year = today.getFullYear();
        const month = String(today.getMonth() + 1).padStart(2, '0');
        const day = String(today.getDate()).padStart(2, '0');
        
        const response = await fetch(
          `https://divinum-officium.herokuapp.com/api/v1/1960/${year}-${month}-${day}/laudes/pt`
        );
        
        if (!response.ok) {
          throw new Error('Erro ao carregar as Laudes');
        }
        
        const data = await response.json();
        setLaudes(data);
        setLoading(false);
      } catch (err) {
        setError('Não foi possível carregar as Laudes. Por favor, tente novamente mais tarde.');
        setLoading(false);
      }
    };

    fetchLaudes();
  }, []);

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Laudes (Oração da Manhã)',
        text: `Reze as Laudes de hoje.`,
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      alert("Compartilhamento não suportado neste navegador.");
    }
  };

  if (loading) {
    return (
      <Container>
        <Title>Laudes</Title>
        <LoadingSpinner
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Title>Laudes</Title>
        <ErrorMessage>{error}</ErrorMessage>
      </Container>
    );
  }

  return (
    <Container>
      <Helmet>
        <title>Laudes - Apenas um Católico</title>
        <meta name="description" content="Acompanhe as Laudes da Liturgia das Horas" />
      </Helmet>

      <Title>Laudes</Title>

      <DataLaudes>
        {new Date().toLocaleDateString('pt-BR', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}
      </DataLaudes>

      {laudes && (
        <>
          {laudes.invitatorium && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <h2>Invitátorio</h2>
              <p>{laudes.invitatorium.ant}</p>
              <p>{laudes.invitatorium.psalm}</p>
            </LaudesCard>
          )}

          {laudes.hymn && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <h2>Hino</h2>
              <p>{laudes.hymn}</p>
            </LaudesCard>
          )}

          {laudes.psalms && laudes.psalms.map((psalm, index) => (
            <LaudesCard
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.3 + (index * 0.1) }}
            >
              <h2>Salmo {index + 1}</h2>
              <p className="rubrica">{psalm.ant}</p>
              <p>{psalm.psalm}</p>
            </LaudesCard>
          ))}

          {laudes.lectioBrevis && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.6 }}
            >
              <h2>Leitura Breve</h2>
              <p>{laudes.lectioBrevis.reading}</p>
              <p className="rubrica">{laudes.lectioBrevis.reference}</p>
            </LaudesCard>
          )}

          {laudes.benedictus && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.7 }}
            >
              <h2>Benedictus</h2>
              <p className="rubrica">{laudes.benedictus.ant}</p>
              <p>{laudes.benedictus.psalm}</p>
            </LaudesCard>
          )}

          {laudes.preces && laudes.preces.length > 0 && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.8 }}
            >
              <h2>Preces</h2>
              {laudes.preces.map((prece, index) => (
                <p key={index}>{prece}</p>
              ))}
            </LaudesCard>
          )}

          {laudes.oratio && (
            <LaudesCard
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.9 }}
            >
              <h2>Oração Conclusiva</h2>
              <p>{laudes.oratio}</p>
            </LaudesCard>
          )}
        </>
      )}
    </Container>
  );
};

export default Laudes;
