import { useState, useEffect } from 'react';

const useDeviceType = () => {
  // Correção: Não acessar window diretamente no estado inicial
  const [isMobile, setIsMobile] = useState(false);
  const [isTablet, setIsTablet] = useState(false);
  const [isDesktop, setIsDesktop] = useState(true);

  useEffect(() => {
    // Função para determinar o tipo de dispositivo
    const getDeviceType = () => {
      if (typeof window === 'undefined') return { isMobile: false, isTablet: false, isDesktop: true };
      
      const width = window.innerWidth;
      const mobile = width <= 768;
      const tablet = width > 768 && width <= 1024;
      const desktop = width > 1024;
      
      return { isMobile: mobile, isTablet: tablet, isDesktop: desktop };
    };

    // Configurar estado inicial após o componente montar
    const initialDeviceType = getDeviceType();
    setIsMobile(initialDeviceType.isMobile);
    setIsTablet(initialDeviceType.isTablet);
    setIsDesktop(initialDeviceType.isDesktop);

    const handleResize = () => {
      const deviceType = getDeviceType();
      setIsMobile(deviceType.isMobile);
      setIsTablet(deviceType.isTablet);
      setIsDesktop(deviceType.isDesktop);
    };

    window.addEventListener('resize', handleResize);
    
    // Cleanup
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return { isMobile, isTablet, isDesktop };
};

export default useDeviceType;
