import React, { useState, useRef } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { Helmet } from 'react-helmet';
import { motion } from 'framer-motion';
import { FiVolume2, FiVolumeX } from 'react-icons/fi';
import YouTube from 'react-youtube';

const GlobalStyle = createGlobalStyle`
  body {
    overflow-x: hidden;
    width: 100%;
    position: relative;
    
    @media (max-width: 768px) {
      overflow-x: hidden;
      width: 100vw;
    }
  }
`;

const PostContainer = styled.div`
  max-width: 800px;
  width: 100%;
  margin: 80px auto 40px;
  padding: 0 20px;
  color: #666;
  font-size: 15px;
  line-height: 1.6;
  box-sizing: border-box;
  overflow-x: hidden;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    margin: 60px auto 20px;
    padding: 0 16px;
  }

  h1 {
    font-size: 32px;
    color: #1B3358;
    margin-bottom: 24px;
    line-height: 1.2;
    word-wrap: break-word;
    
    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
      font-size: 24px;
      margin-bottom: 16px;
    }
  }

  p {
    margin-bottom: 16px;
    overflow-wrap: break-word;
    word-wrap: break-word;
    hyphens: auto;
  }

  ul {
    margin-bottom: 16px;
    padding-left: 20px;
    
    li {
      margin-bottom: 8px;
      word-wrap: break-word;
    }
  }
`;

const AudioContainer = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  z-index: 1000;
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    bottom: 16px;
    right: 16px;
  }
`;

const MuteButton = styled.button`
  background: #1B3358;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 14px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.2);
  
  &:hover {
    background: #2a4a7f;
  }
  
  svg {
    font-size: 18px;
  }
`;

const Content = styled.div`
  font-size: 1.2rem;
  line-height: 1.8;
  color: ${({ theme }) => theme.colors.text};
  width: 100%;
  max-width: 100%;
  box-sizing: border-box;
  overflow-wrap: break-word;
  word-wrap: break-word;
  word-break: break-word;

  @media (max-width: 768px) {
    font-size: 15px;
    line-height: 1.5;
    color: #666;
  }

  p {
    margin-bottom: 1.5rem;
    width: 100%;
    max-width: 100%;
    box-sizing: border-box;
    overflow-wrap: break-word;
    word-wrap: break-word;
    word-break: break-word;

    @media (max-width: 768px) {
      margin-bottom: 16px;
    }
  }

  blockquote {
    margin: 2rem 0;
    padding: 1rem 2rem;
    border-left: 4px solid ${({ theme }) => theme.colors.secondary};
    background: ${({ theme }) => theme.isDark 
      ? 'rgba(229, 198, 151, 0.1)' 
      : 'rgba(192, 149, 83, 0.1)'};
    font-style: italic;
    color: ${({ theme }) => theme.colors.text};
    width: 100%;
    max-width: 100%;
    box-sizing: border-box;
    overflow-wrap: break-word;
    word-wrap: break-word;
    word-break: break-word;

    @media (max-width: 768px) {
      margin: 16px 0;
      padding: 12px 16px;
      background: #f5f5f5;
      border-left: 3px solid #002B5B;
      color: #444;
    }
  }

  ul {
    margin: 1.5rem 0;
    padding-left: 1.5rem;
    width: 100%;
    max-width: 100%;
    box-sizing: border-box;

    @media (max-width: 768px) {
      margin: 16px 0;
      padding-left: 16px;
    }

    li {
      margin-bottom: 1rem;
      position: relative;
      width: 100%;
      max-width: 100%;
      box-sizing: border-box;
      
      @media (max-width: 768px) {
        margin-bottom: 12px;
      }

      &::before {
        content: "â€¢";
        color: ${({ theme }) => theme.colors.secondary};
        font-weight: bold;
        position: absolute;
