import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Section = styled.section`
  padding: ${props => props.theme.spacing.xxl} 0;
  max-width: 1200px;
  margin: 0 auto;
  padding-left: ${props => props.theme.spacing.lg};
  padding-right: ${props => props.theme.spacing.lg};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: ${props => props.theme.spacing.xl} ${props => props.theme.spacing.md};
  }
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  font-size: clamp(2rem, 5vw, 3rem);
  color: ${props => props.theme.colors.titleColor};
  position: relative;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.5rem;
    margin-bottom: ${props => props.theme.spacing.lg};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const SectionParagraph = styled(motion.p)`
  text-align: center;
  font-size: 1.1rem;
  line-height: 1.8;
  color: ${props => props.theme.colors.textLight};
  margin-bottom: ${props => props.theme.spacing.lg};
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1rem;
    margin-bottom: ${props => props.theme.spacing.md};
  }
`;

const Versiculo = styled(motion.div)`
  text-align: center;
  margin: ${props => props.theme.spacing.xl} 0;
  padding: ${props => props.theme.spacing.lg};
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  border-left: 4px solid ${props => props.theme.colors.secondary};
  box-shadow: ${props => props.theme.shadows.light};
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;

  p {
    font-size: 1.2rem;
    font-style: italic;
    color: ${props => props.theme.colors.text};
    margin-bottom: ${props => props.theme.spacing.sm};
    line-height: 1.6;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 1rem;
    }
  }

  a {
    color: ${props => props.theme.colors.accent};
    text-decoration: none;
    font-weight: 500;
    transition: color ${props => props.theme.transitions.normal};

    &:hover {
      color: ${props => props.theme.colors.secondary};
    }
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: ${props => props.theme.spacing.xxl} 0;

  .line {
    flex: 1;
    max-width: 300px;
    height: 2px;
    background: ${props => props.theme.colors.secondary};
    margin: 0 ${props => props.theme.spacing.md};
  }

  .cross {
    color: ${props => props.theme.colors.secondary};
    font-size: 1.5rem;
    font-weight: bold;
  }
`;

const BiblicalSection = () => {
  return (
    <Section>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        O que a Bíblia diz sobre imagens?
      </SectionTitle>
      
      <SectionParagraph
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
        viewport={{ once: true }}
      >
        As imagens nos ajudam a lembrar das obras de Deus e dos santos que seguiram a Cristo.
      </SectionParagraph>
      
      <Versiculo
        initial={{ opacity: 0, scale: 0.9 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        <p>
          "Faze para ti imagens de querubins..."
        </p>
        <a 
          href="https://www.bibliaonline.com.br/nvi/ex/25/18" 
          target="_blank" 
          rel="noopener noreferrer"
        >
          (Êxodo 25:18)
        </a>
      </Versiculo>

      <Divider>
        <div className="line"></div>
        <span className="cross">✝</span>
        <div className="line"></div>
      </Divider>

      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        Por que veneramos os Santos?
      </SectionTitle>

      <SectionParagraph
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, delay: 0.2 }}
        viewport={{ once: true }}
      >
        Homens e mulheres de fé que trilharam os caminhos de Jesus, os santos deixaram um legado de amor, 
        serviço e dedicação que transcende o tempo e continua a inspirar milhões.
      </SectionParagraph>
    </Section>
  );
};

export default BiblicalSection;
