import styled from 'styled-components';
import { FiInstagram, FiMail, FiGithub } from 'react-icons/fi';

const FooterContainer = styled.footer`
  background: ${props => props.theme.colors.footerBg};
  color: ${props => props.theme.colors.white};
  padding: 40px 16px;
  width: 100%;
  box-sizing: border-box;
  overflow: hidden;
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 24px 16px;
  }
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 24px;
  text-align: center;
  padding: 0 16px;
  
  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 16px;
  }
`;

const SocialLinks = styled.div`
  display: flex;
  justify-content: center;
  gap: 24px;
  margin-bottom: 16px;
  
  a {
    color: ${props => props.theme.colors.white};
    font-size: 24px;
    transition: color 0.2s;
    display: flex;
    align-items: center;
    
    &:hover {
      color: ${props => props.theme.colors.secondary};
    }
    
    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 20px;
    }
  }
`;

const Copyright = styled.p`
  font-size: 14px;
  opacity: 0.8;
  margin: 0;
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <SocialLinks>
          <a href="https://www.instagram.com/apenas_um_catolico/" target="_blank" rel="noopener noreferrer">
            <FiInstagram />
          </a>
          <a href="mailto:joao.carlosdev23@gmail.com">
            <FiMail />
          </a>
          <a href="https://github.com/JoaocarlosTeco" target="_blank" rel="noopener noreferrer">
            <FiGithub />
          </a>
        </SocialLinks>
        <Copyright>
          © {new Date().getFullYear()} Apenas um Católico. Todos os direitos reservados.
        </Copyright>
      </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
