import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiArrowRight, FiX, FiHeart, FiBookOpen } from 'react-icons/fi';

const HomeContainer = styled.div`
  min-height: 100vh;
`;

const HeroSection = styled.section`
  position: relative;
  height: 80vh;
  display: flex;
  align-items: center;
  justify-content: flex-start;
  text-align: left;
  color: ${({ theme }) => theme.colors.white};
  overflow: hidden;
  background-image: url(/Fotos/clouds-bg.png);
  background-size: cover;
  background-position: center;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      to right,
      rgba(13, 42, 79, 0.8) 0%,
      rgba(13, 42, 79, 0.4) 50%,
      transparent 100%
    );
    z-index: 1;
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.tablet}) {
    height: 85vh;
    background-image: url(/Fotos/hero-jesus.png);
    justify-content: center;
    text-align: center;

    &::before {
      background: rgba(0, 0, 0, 0.4);
    }
  }
`;

const ForegroundJesusImage = styled(motion.img)`
  position: absolute;
  right: 5%;
  bottom: 0;
  height: 95%;
  width: auto;
  object-fit: contain;
  object-position: bottom right;
  z-index: 0;

  @media (max-width: ${({ theme }) => theme.breakpoints.tablet}) {
    display: none;
  }
`;

const HeroContent = styled(motion.div)`
  position: relative;
  z-index: 2;
  max-width: 50%;
  padding: 0 5%;
  width: 100%;
  text-align: left;

  @media (max-width: ${({ theme }) => theme.breakpoints.tablet}) {
    max-width: 90%;
    padding: ${({ theme }) => theme.spacing.xl};
    background: rgba(0, 0, 0, 0.3);
    border-radius: 16px;
    backdrop-filter: blur(5px);
  }
`;

const HeroTitle = styled(motion.h1)`
  font-size: clamp(2.5rem, 6vw, 4rem);
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  color: ${({ theme }) => theme.colors.white};

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    font-size: 2.2rem;
  }
`;

const HeroSubtitle = styled(motion.p)`
  font-size: clamp(1.1rem, 3vw, 1.3rem);
  margin-bottom: ${({ theme }) => theme.spacing.xl};
  opacity: 0.9;
  line-height: 1.6;
  color: ${({ theme }) => theme.colors.white};
`;

const HeroButton = styled(motion(Link))`
  display: inline-flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing.sm};
  background: ${({ theme }) => theme.colors.secondary};
  color: ${({ theme }) => theme.colors.white};
  padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.xl};
  border-radius: 50px;
  text-decoration: none;
  font-weight: 600;
  transition: all ${({ theme }) => theme.transitions.normal};
  box-shadow: ${({ theme }) => theme.shadows.medium};
  
  &:hover {
    background: ${({ theme }) => theme.colors.primary};
    transform: translateY(-2px);
    box-shadow: ${({ theme }) => theme.shadows.heavy};
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    display: none;
  }
`;

const Section = styled.section`
  padding: ${props => props.theme.spacing.xxl} 0;
  max-width: 1200px;
  margin: 0 auto;
  padding-left: ${props => props.theme.spacing.lg};
  padding-right: ${props => props.theme.spacing.lg};
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  font-size: clamp(2rem, 5vw, 3rem);
`;

const SectionParagraph = styled(motion.p)`
  text-align: center; 
  font-size: 1.1rem; 
  max-width: 800px; 
  margin: -1.5rem auto 2rem auto;
  color: ${props => props.theme.colors.textLight};
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin: ${props => props.theme.spacing.xl} 0;
  gap: ${props => props.theme.spacing.md};
  
  .line {
    flex: 1;
    max-width: 200px;
    height: 2px;
    background: ${props => props.theme.colors.secondary};
  }
  
  .cross {
    color: ${props => props.theme.colors.secondary};
    font-size: 2rem;
  }
`;

const Versiculo = styled(motion.div)`
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.accent});
  color: ${props => props.theme.colors.white};
  padding: ${props => props.theme.spacing.xl};
  border-radius: 15px;
  text-align: center;
  margin: ${props => props.theme.spacing.xl} 0;
  box-shadow: ${props => props.theme.shadows.medium};
  
  p {
    font-size: 1.2rem;
    font-style: italic;
    margin-bottom: ${props => props.theme.spacing.md};
  }
  
  a {
    color: ${props => props.theme.colors.secondary};
    font-weight: 600;
    
    &:hover {
      color: ${props => props.theme.colors.gold};
    }
  }
`;

const SantosGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${({ theme }) => theme.spacing.lg};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: ${({ theme }) => theme.spacing.md};
  }
`;

const SantoCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: ${({ theme }) => theme.borderRadius};
  overflow: hidden;
  box-shadow: ${({ theme }) => theme.shadows.medium};
  transition: all ${({ theme }) => theme.transitions.normal};
  cursor: pointer;
  
  a {
    text-decoration: none;
    color: inherit;
    display: block;
  }
  
  &:hover {
    box-shadow: ${({ theme }) => theme.shadows.heavy};
    transform: translateY(-5px);
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    width: 90%;
    margin: 0 auto;
  }
`;

const SantoImage = styled.img`
  width: 100%;
  height: auto;
  object-fit: contain;
  border-bottom: 3px solid ${({ theme }) => theme.colors.secondary};

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    height: 180px;
  }
`;

const SantoInfo = styled.div`
  padding: ${props => props.theme.spacing.lg};
  text-align: center;
  
  h3 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: ${props => props.theme.spacing.sm};
  }
  
  p {
    color: ${props => props.theme.colors.textLight};
    font-size: 0.9rem;
  }
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  margin-top: ${props => props.theme.spacing.xl};
`;

const FeatureCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  padding: ${props => props.theme.spacing.xl};
  border-radius: 15px;
  text-align: center;
  box-shadow: ${props => props.theme.shadows.light};
  transition: all ${props => props.theme.transitions.normal};
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.medium};
  }
  
  .icon {
    font-size: 3rem;
    color: ${props => props.theme.colors.secondary};
    margin-bottom: ${props => props.theme.spacing.lg};
  }
  
  h3 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: ${props => props.theme.spacing.md};
  }
  
  p {
    color: ${props => props.theme.colors.textLight};
    line-height: 1.6;
  }
`;

const Home = () => {
  const santos = [
    {
      id: 4,
      name: 'São Francisco de Assis',
      image: '/Fotos/São franciso.jpg',
      description: 'Padroeiro dos animais e da ecologia',
      link: '/santos/sao-francisco'
    },
    {
      id: 2,
      name: 'Santa Teresinha',
      image: '/Fotos/Santa terezinha.jpg',
      description: 'Doutora da Igreja e padroeira das missões'
    },
    {
      id: 3,
      name: 'São Padre Pio',
      image: '/Fotos/Padre-Pio.webp',
      description: 'Padre estigmatizado e confessor'
    },
    {
      id: 1,
      name: 'Nossa Senhora',
      image: '/Fotos/Nossa senhora.webp',
      description: 'Mãe de Jesus e nossa intercessora'
    }
  ];

  const features = [
    {
      icon: <FiX />,
      title: 'Orações Diárias',
      description: 'Acesse orações tradicionais e contemporâneas para fortalecer sua fé diariamente.'
    },
    {
      icon: <FiBookOpen />,
      title: 'Histórias dos Santos',
      description: 'Conheça a vida e os milagres dos santos que nos inspiram a seguir Cristo.'
    },
    {
      icon: <FiHeart />,
      title: 'Reflexões Espirituais',
      description: 'Meditações e reflexões para aprofundar sua vida espiritual.'
    }
  ];

  return (
    <HomeContainer>
      <Helmet>
        <title>Início - Apenas um Católico</title>
        <meta name="description" content="Bem-vindo ao site católico dedicado à evangelização e devoção aos santos. Jesus: Nosso Senhor e Salvador." />
      </Helmet>

      <HeroSection>
        <ForegroundJesusImage 
          src="/Fotos/hero-jesus.png" 
          alt="Jesus Cristo" 
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 1.2, ease: "easeOut" }}
        />
        <HeroContent
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <HeroTitle
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Jesus: Nosso Senhor e Salvador
          </HeroTitle>
          <HeroSubtitle
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            "Eu sou o caminho, a verdade e a vida. Ninguém vem ao Pai senão por mim."
          </HeroSubtitle>
          <HeroButton
            to="/santos"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Saiba mais
          </HeroButton>
        </HeroContent>
      </HeroSection>

      <Section>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          O que a Bíblia diz sobre imagens?
        </SectionTitle>
        <SectionParagraph
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
           As imagens nos ajudam a lembrar das obras de Deus e dos santos que seguiram a Cristo.
        </SectionParagraph>
        <Versiculo
          initial={{ opacity: 0, scale: 0.9 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          <p>
            "Faze para ti imagens de querubins..."
          </p>
          <a 
            href="https://www.bibliaonline.com.br/nvi/ex/25/18" 
            target="_blank" 
            rel="noopener noreferrer"
          >
            (Êxodo 25:18)
          </a>
        </Versiculo>

        <Divider>
          <div className="line"></div>
          <span className="cross">✝</span>
          <div className="line"></div>
        </Divider>

        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Por que veneramos os Santos?
        </SectionTitle>

        <SectionParagraph
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          Homens e mulheres de fé que trilharam os caminhos de Jesus, os santos deixaram um legado de amor, 
          serviço e dedicação que transcende o tempo e continua a inspirar milhões.
        </SectionParagraph>
      </Section>

      <Section>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          História dos Santos
        </SectionTitle>

        <SantosGrid
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          viewport={{ once: true }}
        >
          {santos.map((santo, index) => (
            <SantoCard
              key={santo.id}
              whileHover={{ y: -10 }}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              {santo.link ? (
                <Link to={santo.link}>
                  <SantoImage src={santo.image} alt={santo.name} />
                  <SantoInfo>
                    <h3>{santo.name}</h3>
                    <p>{santo.description}</p>
                  </SantoInfo>
                </Link>
              ) : (
                <>
                  <SantoImage src={santo.image} alt={santo.name} />
                  <SantoInfo>
                    <h3>{santo.name}</h3>
                    <p>{santo.description}</p>
                  </SantoInfo>
                </>
              )}
            </SantoCard>
          ))}
        </SantosGrid>
      </Section>

      <Section>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Recursos Espirituais
        </SectionTitle>

        <FeaturesGrid>
          {features.map((feature, index) => (
            <FeatureCard
              key={index}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.2 }}
              viewport={{ once: true }}
            >
              <div className="icon">{feature.icon}</div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
            </FeatureCard>
          ))}
        </FeaturesGrid>
      </Section>
    </HomeContainer>
  );
};

export default Home;
