import { createGlobalStyle } from 'styled-components';

export const lightTheme = {
  colors: {
    primary: '#0D2A4F',
    secondary: '#C09553',
    accent: '#8E9BBD',
    text: '#2C2C2C',
    textLight: '#6c757d',
    background: '#FBF9F3',
    white: '#ffffff',
    error: '#dc3545',
    success: '#28a745',
    warning: '#ffc107',
    cardBg: '#FFFFFF',
    headerBg: 'rgba(251, 249, 243, 0.85)',
    footerBg: '#0D2A4F',
    borderColor: '#E0E0E0',
    imagePlaceholder: '#f0f0f0',
  },
  fonts: {
    primary: "'Inter', sans-serif",
    secondary: "'Playfair Display', serif"
  },
  breakpoints: {
    mobile: '768px',
    tablet: '1024px',
    desktop: '1200px'
  },
  spacing: {
    xs: '0.25rem',
    sm: '0.5rem',
    md: '1rem',
    lg: '1.5rem',
    xl: '2rem',
    xxl: '3rem'
  },
  shadows: {
    light: '0 2px 8px rgba(0,0,0,0.08)',
    medium: '0 4px 12px rgba(0,0,0,0.12)',
    heavy: '0 8px 24px rgba(0,0,0,0.16)'
  },
  transitions: {
    fast: '0.2s ease',
    normal: '0.3s ease',
    slow: '0.5s ease'
  }
};

export const darkTheme = {
  ...lightTheme,
  colors: {
    ...lightTheme.colors,
    primary: '#D4AF7A',
    secondary: '#E5C697',
    accent: '#A6B0C7',
    text: '#FFFFFF',
    textLight: '#D1D1D1',
    background: '#121212',
    cardBg: '#1E1E1E',
    headerBg: 'rgba(18, 18, 18, 0.95)',
    footerBg: '#1E1E1E',
    borderColor: '#2A2A2A',
    imagePlaceholder: '#2C2C2C',
    white: '#FFFFFF',
    error: '#ff6b6b',
    success: '#51cf66',
    warning: '#ffd43b',
  },
  shadows: {
    light: '0 2px 8px rgba(0,0,0,0.2)',
    medium: '0 4px 12px rgba(0,0,0,0.3)',
    heavy: '0 8px 24px rgba(0,0,0,0.4)'
  }
};

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html, body {
    width: 100%;
    max-width: 100vw;
    overflow-x: hidden;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    scroll-behavior: smooth;
  }

  body {
    background: ${props => props.theme.colors.background};
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    min-height: 100vh;
    position: relative;
  }

  #root {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    overflow-x: hidden;
  }

  main {
    flex: 1;
    width: 100%;
    max-width: 100%;
    overflow-x: hidden;
  }

  img {
    max-width: 100%;
    height: auto;
    display: block;
  }

  a {
    text-decoration: none;
    color: inherit;
  }

  button {
    cursor: pointer;
    border: none;
    background: none;
    font-family: inherit;
  }

  ul, ol {
    list-style: none;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: ${({ theme }) => theme.fonts.secondary};
    font-weight: 600;
    line-height: 1.2;
    margin-bottom: ${({ theme }) => theme.spacing.md};
    color: ${({ theme }) => theme.colors.text};
  }

  /* Tipografia Responsiva */
  h1 {
    font-size: clamp(24px, 5vw, 40px);
    letter-spacing: -0.02em;
    margin-bottom: ${({ theme }) => theme.spacing.lg};
  }

  h2 {
    font-size: clamp(20px, 4vw, 28px);
    letter-spacing: -0.01em;
    margin-bottom: ${({ theme }) => theme.spacing.md};
  }

  h3 {
    font-size: clamp(18px, 3vw, 24px);
    letter-spacing: -0.01em;
    margin-bottom: ${({ theme }) => theme.spacing.md};
  }

  p {
    font-size: clamp(14px, 2vw, 18px);
    line-height: 1.6;
    margin-bottom: ${({ theme }) => theme.spacing.md};
    color: ${({ theme }) => theme.colors.textLight};
  }

  .small-text {
    font-size: clamp(12px, 1.5vw, 14px);
    line-height: 1.4;
    color: ${({ theme }) => theme.colors.textLight};
  }

  /* Ajustes adicionais para melhor legibilidade */
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    h1 {
      line-height: 1.3;
    }

    h2, h3 {
      line-height: 1.4;
    }

    p {
      line-height: 1.7;
    }
  }

  a {
    color: ${({ theme }) => theme.colors.accent};
    transition: color ${({ theme }) => theme.transitions.normal};
    
    &:hover {
      color: ${({ theme }) => theme.colors.primary};
    }
  }

  /* Scrollbar personalizada */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: ${({ theme }) => theme.colors.background};
  }

  ::-webkit-scrollbar-thumb {
    background: ${({ theme }) => theme.colors.secondary};
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: ${({ theme }) => theme.colors.primary};
  }

  /* Seleção de texto */
  ::selection {
    background: ${({ theme }) => theme.colors.secondary};
    color: ${({ theme }) => theme.colors.text};
  }

  /* Focus visible para acessibilidade */
  :focus-visible {
    outline: 2px solid ${({ theme }) => theme.colors.secondary};
    outline-offset: 2px;
  }

  /* Animações de entrada */
  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideIn {
    from {
      transform: translateX(-100%);
    }
    to {
      transform: translateX(0);
    }
  }

  .fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .slide-in {
    animation: slideIn 0.4s ease-out;
  }

  /* Ajustes específicos para o tema escuro */
  body.dark-theme {
    img {
      filter: brightness(0.9);
    }
    
    .card {
      background: ${({ theme }) => theme.colors.cardBg};
      border: 1px solid ${({ theme }) => theme.colors.borderColor};
    }
  }

  /* Melhorias na scrollbar para tema escuro */
  body.dark-theme::-webkit-scrollbar-track {
    background: ${({ theme }) => theme.colors.background};
  }

  body.dark-theme::-webkit-scrollbar-thumb {
    background: ${({ theme }) => theme.colors.primary};
    
    &:hover {
      background: ${({ theme }) => theme.colors.secondary};
    }
  }

  /* Seleção de texto no tema escuro */
  body.dark-theme ::selection {
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
  }
`;

export default GlobalStyle;
