import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import Hero from '../../components/Hero';

const HeroContainer = styled.section`
  position: relative;
  min-height: calc(100vh - 75px);
  margin-top: 75px;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px 20px;
  overflow: hidden;
  background-image: url('/images/heroes/hero-jesus.png');
  background-size: 80%;
  background-position: center 60%;
  background-repeat: no-repeat;
  background-attachment: fixed;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      180deg,
      rgba(27, 51, 88, 0.8) 0%,
      rgba(27, 51, 88, 0.7) 50%,
      rgba(27, 51, 88, 0.85) 100%
    );
    z-index: 1;
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    min-height: calc(100vh - 65px);
    margin-top: 65px;
    background-attachment: scroll;
    background-size: cover;
    background-position: center 35%;
    padding: 30px 15px;
  }

  @media (max-width: 480px) {
    min-height: calc(100vh - 65px);
    background-position: center 30%;
    background-size: cover;
  }

  /* Ajuste específico para telas muito grandes */
  @media (min-width: 1400px) {
    background-size: 70%;
    background-position: center 65%;
  }

  /* Ajuste para tablets */
  @media (min-width: 769px) and (max-width: 1024px) {
    background-size: 85%;
    background-position: center 55%;
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 2;
  max-width: 1000px;
  width: 100%;
  text-align: center;
  padding: 0 20px;
  opacity: 0;
  animation: fadeIn 1.5s ease forwards;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    padding: 0 15px;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const HeroText = styled.div`
  h1 {
    font-size: clamp(28px, 8vw, 56px);
    color: white;
    margin-bottom: 20px;
    line-height: 1.2;
    text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7);
    font-weight: 700;
  }

  p {
    font-size: clamp(16px, 3vw, 20px);
    color: white;
    margin-bottom: 30px;
    line-height: 1.6;
    opacity: 0.95;
    max-width: 700px;
    margin-left: auto;
    margin-right: auto;
    text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.6);
  }
`;

const Logo = styled.img`
  width: 100%;
  max-width: 300px;
  height: auto;
  margin-bottom: 25px;
  filter: drop-shadow(2px 2px 8px rgba(0, 0, 0, 0.8));
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    max-width: 220px;
    margin-bottom: 20px;
  }

  @media (max-width: 480px) {
    max-width: 180px;
    margin-bottom: 15px;
  }
`;

const HeroSection = () => {
  return (
    <HeroContainer>
      <HeroContent>
        <Logo src="/images/logos/Logo tipo apenas um catolico.png" alt="Apenas um Católico" />
        <HeroText>
          <h1>Jesus:<br />Nosso Senhor e Salvador</h1>
          <p>"Eu sou o caminho, a verdade e a vida. Ninguém vem ao Pai senão por mim."</p>
        </HeroText>
      </HeroContent>
    </HeroContainer>
  );
};

export default HeroSection;
