import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 40px 20px;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const LoadingSpinner = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.theme.colors.background};
  border-top: 3px solid ${props => props.theme.colors.secondary};
  border-radius: 50%;
  margin: 20px auto;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: ${props => props.theme.colors.error};
  padding: 20px;
  background: ${props => props.theme.colors.background};
  border-radius: 8px;
  margin: 20px 0;
`;

const LiturgiaCard = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: 15px;
  padding: 24px;
  margin-bottom: 24px;
  box-shadow: ${props => props.theme.shadows.light};

  h2 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: 16px;
    font-size: 1.5rem;
  }

  h3 {
    color: ${props => props.theme.colors.secondary};
    margin: 24px 0 12px;
    font-size: 1.3rem;
  }

  p {
    color: ${props => props.theme.colors.text};
    line-height: 1.8;
    margin-bottom: 16px;
    font-size: 1.1rem;
  }

  .referencia {
    color: ${props => props.theme.colors.textLight};
    font-style: italic;
    font-size: 0.9rem;
    text-align: right;
  }
`;

const DataLiturgia = styled.div`
  text-align: center;
  margin-bottom: 24px;
  color: ${props => props.theme.colors.textLight};
  font-size: 1.1rem;
`;

const ActionsContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const ActionButton = styled.button`
  background: transparent;
  border: 1px solid ${({ theme }) => theme.colors.borderColor};
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: ${({ theme }) => theme.colors.textLight};

  &:hover {
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
    border-color: ${({ theme }) => theme.colors.primary};
  }
`;

const TabsContainer = styled.div`
  display: flex;
  border: 1px solid ${({ theme }) => theme.colors.borderColor};
  border-radius: 8px;
  overflow: hidden;
  margin-bottom: 2rem;
`;

const TabButton = styled.button`
  flex: 1;
  padding: 1rem;
  font-size: 1rem;
  font-weight: 500;
  border: none;
  background: ${({ active, theme }) => (active ? theme.colors.primary : 'transparent')};
  color: ${({ active, theme }) => (active ? theme.colors.white : theme.colors.textLight)};
  cursor: pointer;
  transition: all 0.3s ease;

  &:not(:last-child) {
    border-right: 1px solid ${({ theme }) => theme.colors.borderColor};
  }
`;

const Content = styled.div`
  font-size: ${({ fontSize }) => fontSize}px;
  line-height: 1.8;
  color: ${({ theme }) => theme.colors.textLight};
  
  h2, h3 {
    color: ${({ theme }) => theme.colors.text};
    margin-bottom: 1rem;
  }
`;

const LiturgiaDiaria = () => {
  const [liturgia, setLiturgia] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);

  useEffect(() => {
    const fetchLiturgia = async () => {
      try {
        const response = await fetch('https://liturgia.up.railway.app/');
        if (!response.ok) {
          throw new Error('Erro ao carregar a liturgia');
        }
        const data = await response.json();
        console.log('Dados da API:', data); // Para debug
        setLiturgia(data);
        setLoading(false);
      } catch (err) {
        console.error('Erro ao buscar liturgia:', err); // Para debug
        setError('Não foi possível carregar a liturgia. Por favor, tente novamente mais tarde.');
        setLoading(false);
      }
    };

    fetchLiturgia();
  }, []);

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Liturgia Diária',
        text: `Liturgia de ${liturgia?.data}`,
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      alert("Compartilhamento não suportado neste navegador.");
    }
  };

  if (loading) {
    return (
      <Container>
        <Title>Liturgia Diária</Title>
        <LoadingSpinner
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Title>Liturgia Diária</Title>
        <ErrorMessage>{error}</ErrorMessage>
      </Container>
    );
  }

  // Estrutura de dados esperada da API
  const leituras = [
    { titulo: 'Primeira Leitura', texto: liturgia?.primeiraLeitura?.texto, referencia: liturgia?.primeiraLeitura?.referencia },
    { titulo: 'Salmo Responsorial', texto: liturgia?.salmo?.texto, referencia: liturgia?.salmo?.referencia },
    { titulo: 'Segunda Leitura', texto: liturgia?.segundaLeitura?.texto, referencia: liturgia?.segundaLeitura?.referencia },
    { titulo: 'Evangelho', texto: liturgia?.evangelho?.texto, referencia: liturgia?.evangelho?.referencia }
  ].filter(leitura => leitura.texto && leitura.referencia); // Remove leituras vazias

  return (
    <Container>
      <Helmet>
        <title>Liturgia Diária - Apenas um Católico</title>
        <meta name="description" content="Acompanhe a liturgia diária da Igreja Católica" />
      </Helmet>

      <Title>Liturgia Diária</Title>

      <DataLiturgia>
        {new Date().toLocaleDateString('pt-BR', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}
      </DataLiturgia>

      {liturgia && leituras.map((leitura, index) => (
        <LiturgiaCard
          key={index}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: index * 0.2 }}
        >
          <h2>{leitura.titulo}</h2>
          <p>{leitura.texto}</p>
          <p className="referencia">{leitura.referencia}</p>
        </LiturgiaCard>
      ))}

      {liturgia && leituras.length === 0 && (
        <ErrorMessage>
          Não há leituras disponíveis para hoje. Por favor, tente novamente mais tarde.
        </ErrorMessage>
      )}

      <ActionsContainer>
        <ActionButton onClick={() => setFontSize(s => Math.max(s - 1, 12))} aria-label="Diminuir fonte">
          <FiMinus />
        </ActionButton>
        <ActionButton onClick={() => setFontSize(s => Math.min(s + 1, 24))} aria-label="Aumentar fonte">
          <FiPlus />
        </ActionButton>
        <ActionButton onClick={handleShare} aria-label="Compartilhar">
          <FiShare2 />
        </ActionButton>
      </ActionsContainer>
    </Container>
  );
};

export default LiturgiaDiaria;
