import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

// Componentes
import Header from './components/Header';
import Footer from './components/Footer';
import LoadingScreen from './components/LoadingScreen';

// Páginas
import Home from './pages/Home';
import Santos from './pages/Santos';
import SaoFrancisco from './pages/SaoFrancisco';
import Oracao from './pages/Oracao';
import Calendario from './pages/Calendario';
import Blog from './pages/Blog';
import Sobre from './pages/Sobre';

// Context
import { ThemeContext } from './context/ThemeContext';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  background: ${props => props.theme.colors.background};
  transition: background-color ${props => props.theme.transitions.normal};
`;

const Main = styled(motion.main)`
  flex: 1;
  padding-top: 80px; // Espaço para o header fixo
`;

const pageVariants = {
  initial: {
    opacity: 0,
    y: 20
  },
  in: {
    opacity: 1,
    y: 0
  },
  out: {
    opacity: 0,
    y: -20
  }
};

const pageTransition = {
  type: 'tween',
  ease: 'anticipate',
  duration: 0.4
};

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    // Simular carregamento inicial
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <ThemeContext.Provider value={{ isDarkMode, toggleTheme }}>
      <AppContainer>
        <Helmet>
          <title>Apenas um Católico - Evangelização e Devoção</title>
          <meta name="description" content="Site católico dedicado à evangelização e devoção aos santos. Conheça a história dos santos, orações e reflexões espirituais." />
        </Helmet>

        <Header />

        <AnimatePresence mode="wait">
          <Main
            initial="initial"
            animate="in"
            exit="out"
            variants={pageVariants}
            transition={pageTransition}
          >
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/santos" element={<Santos />} />
              <Route path="/santos/sao-francisco" element={<SaoFrancisco />} />
              <Route path="/oracao" element={<Oracao />} />
              <Route path="/calendario" element={<Calendario />} />
              <Route path="/blog" element={<Blog />} />
              <Route path="/sobre" element={<Sobre />} />
            </Routes>
          </Main>
        </AnimatePresence>

        <Footer />
      </AppContainer>
    </ThemeContext.Provider>
  );
}

export default App;
