import React from 'react';
import styled from 'styled-components';
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Card, CardGrid, CardTitle, CardText } from '../components/Card';

const posts = [
  {
    id: 1,
    titulo: 'Como rezar o Terço com devoção',
    resumo: 'Dicas práticas para rezar o terço diariamente e crescer na fé.',
    data: '10/04/2025'
  },
  {
    id: 2,
    titulo: 'A importância da Eucaristia',
    resumo: 'Reflexão sobre o valor da Santa Missa e da comunhão.',
    data: '02/04/2025'
  },
  {
    id: 3,
    titulo: 'O exemplo de Santa Teresinha',
    resumo: 'Como a pequena via pode transformar sua vida espiritual.',
    data: '25/03/2025'
  },
  {
    id: 4,
    title: "Seu celular está destruindo sua vida",
    excerpt: "Uma reflexão sincera sobre como nosso relacionamento com o celular está afetando nossa capacidade de viver o momento presente.",
    date: "15 de Março de 2024",
    path: "/blog/celular"
  }
];

const Container = styled.div`
  max-width: 900px;
  margin: 0 auto;
  padding: 40px 16px 80px 16px;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  padding: 0 ${({ theme }) => theme.spacing.md};

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: 16px;
    padding: 0 16px;
  }
`;

const CardResumo = styled.p`
  color: ${props => props.theme.colors.textLight};
  font-size: 1.1rem;
  margin-bottom: 1.5rem;
  line-height: 1.6;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    font-size: 0.66rem;
    margin-bottom: 0.6rem;
    line-height: 1.4;
  }
`;

const CardData = styled.span`
  color: ${props => props.theme.colors.secondary};
  font-size: 0.95rem;

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    font-size: 0.57rem;
  }
`;

const PlaceholderText = styled.p`
  color: ${({ theme }) => theme.colors.textLight};
  font-style: italic;
  text-align: center;
  margin-top: 2rem;
  font-size: 1.1rem;
`;

const BlogContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 40px 20px;
  opacity: 0;
  animation: fadeIn 1s ease forwards;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 10px;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const BlogCard = styled(Card)`
  position: relative;
  cursor: pointer;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: ${props => props.theme.colors.secondary};
    transform: scaleY(0);
    transform-origin: bottom;
    transition: transform ${props => props.theme.transitions.normal};
  }

  &:hover {
    &::before {
      transform: scaleY(1);
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    &::before {
      width: 2px;
    }
  }
`;

const BlogTitle = styled(CardTitle)`
  padding-left: 16px;
  transition: color ${props => props.theme.transitions.normal};

  ${BlogCard}:hover & {
    color: ${props => props.theme.colors.secondary};
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding-left: 10px;
    font-size: 1.1rem;
  }
`;

const BlogExcerpt = styled(CardText)`
  padding-left: 16px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding-left: 10px;
  }
`;

const PostDate = styled.span`
  color: ${props => props.theme.colors.secondary};
  font-size: 0.95rem;
  font-weight: 500;
  padding-left: 16px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.8rem;
    padding-left: 10px;
  }
`;

const Blog = () => (
  <Container>
    <Helmet>
      <title>Blog - Apenas um Católico</title>
      <meta name="description" content="Reflexões, dicas e artigos católicos para sua vida espiritual." />
    </Helmet>
    <Title>Blog Católico</Title>
    <Grid>
      {posts.map(post => (
        <Card key={post.id}>
          <CardTitle>{post.titulo}</CardTitle>
          <CardResumo>{post.resumo}</CardResumo>
          <CardData>{post.data}</CardData>
          <PlaceholderText>
            O nosso querido católico está trabalhando nisso ainda.
          </PlaceholderText>
        </Card>
      ))}
    </Grid>
    <BlogContainer>
      <Title>Blog</Title>
      <CardGrid>
        {posts.map((post) => (
          <BlogCard
            key={post.id}
            to={post.path}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <div>
              <BlogTitle>{post.title}</BlogTitle>
              <BlogExcerpt>{post.excerpt}</BlogExcerpt>
            </div>
            <PostDate>{post.date}</PostDate>
          </BlogCard>
        ))}
      </CardGrid>
    </BlogContainer>
  </Container>
);

export default Blog;
