const sharedStyles = {
  colors: {
    primary: '#1B3358', // Azul marinho
    secondary: '#C09553', // Dourado
    accent: '#4B7BE5', // Azul vibrante
    white: '#FFFFFF',
    error: '#E53E3E',
  },
  transitions: {
    normal: '0.3s ease',
  },
  shadows: {
    light: '0 2px 8px rgba(0, 0, 0, 0.1)',
    medium: '0 4px 12px rgba(0, 0, 0, 0.15)',
    heavy: '0 8px 24px rgba(0, 0, 0, 0.2)',
  }
};

export const desktopStyles = {
  ...sharedStyles,
  spacing: {
    sm: '8px',
    md: '16px',
    lg: '24px',
    xl: '32px',
    xxl: '48px',
  },
  typography: {
    hero: {
      title: '64px',
      subtitle: '24px',
    },
    heading: {
      h1: '48px',
      h2: '36px',
      h3: '24px',
    },
    body: {
      large: '18px',
      normal: '16px',
      small: '14px',
    }
  }
};

export const mobileStyles = {
  ...sharedStyles,
  spacing: {
    sm: '4px',
    md: '8px',
    lg: '16px',
    xl: '24px',
    xxl: '32px',
  },
  typography: {
    hero: {
      title: '28px',
      subtitle: '14px',
    },
    heading: {
      h1: '24px',
      h2: '20px',
      h3: '18px',
    },
    body: {
      large: '16px',
      normal: '14px',
      small: '12px',
    }
  }
};
