import React from 'react';
import { Helmet } from 'react-helmet';

const SEO = ({ 
  title = "Apenas um Católico - Evangelização e Devoção",
  description = "Site católico dedicado à evangelização e devoção aos santos. Conheça a história dos santos, orações e reflexões espirituais.",
  keywords = "católico, santos, orações, evangelização, fé, devoção, religião, cristianismo",
  image = "/images/logos/Logo tipo apenas um catolico.png",
  url = "https://apenas-um-catolico.vercel.app",
  type = "website",
  author = "Apenas um Católico",
  structuredData = null,
  breadcrumbs = [],
  article = null,
  saint = null,
  prayer = null,
  publishedTime = null,
  modifiedTime = null,
  category = null,
  tags = []
}) => {
  const siteTitle = "Apenas um Católico";
  const fullTitle = title === siteTitle ? title : `${title} - ${siteTitle}`;

  // Structured Data para o site
  const websiteSchema = {
    "@context": "https://schema.org",
    "@type": "WebSite",
    "name": siteTitle,
    "description": description,
    "url": url,
    "publisher": {
      "@type": "Organization",
      "name": siteTitle,
      "url": url,
      "logo": {
        "@type": "ImageObject",
        "url": `${url}${image}`,
        "width": 512,
        "height": 512
      },
      "sameAs": [
        "https://www.facebook.com/apenasumcatolico",
        "https://www.instagram.com/apenasumcatolico"
      ]
    },
    "potentialAction": {
      "@type": "SearchAction",
      "target": `${url}/buscar?q={search_term_string}`,
      "query-input": "required name=search_term_string"
    }
  };

  // Structured Data para organização religiosa
  const religiousOrganizationSchema = {
    "@context": "https://schema.org",
    "@type": "ReligiousOrganization",
    "name": siteTitle,
    "description": "Plataforma digital católica dedicada à evangelização, educação religiosa e devoção aos santos",
    "url": url,
    "religion": "Christianity",
    "denomination": "Catholic",
    "founder": author,
    "foundingDate": "2023",
    "mission": "Evangelizar através da tecnologia, compartilhando a vida dos santos e ensinamentos católicos",
    "serviceArea": {
      "@type": "Country",
      "name": "Brasil"
    },
    "audience": {
      "@type": "Audience",
      "audienceType": "Catholics, Christians, Faith seekers"
    }
  };

  // Breadcrumbs Schema
  const breadcrumbSchema = breadcrumbs.length > 0 ? {
    "@context": "https://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": breadcrumbs.map((item, index) => ({
      "@type": "ListItem",
      "position": index + 1,
      "name": item.name,
      "item": `${url}${item.url}`
    }))
  } : null;

  // Schema para artigo/blog post
  const articleSchema = article ? {
    "@context": "https://schema.org",
    "@type": "Article",
    "headline": article.title,
    "description": article.description,
    "image": `${url}${article.image || image}`,
    "author": {
      "@type": "Person",
      "name": article.author || author
    },
    "publisher": {
      "@type": "Organization",
      "name": siteTitle,
      "logo": {
        "@type": "ImageObject",
        "url": `${url}${image}`
      }
    },
    "datePublished": publishedTime,
    "dateModified": modifiedTime || publishedTime,
    "mainEntityOfPage": {
      "@type": "WebPage",
      "@id": url
    },
    "keywords": tags.join(", "),
    "articleSection": category,
    "about": article.about || "Catholic faith, spirituality, saints"
  } : null;

  // Schema específico para santos
  const saintSchema = saint ? {
    "@context": "https://schema.org",
    "@type": "Person",
    "name": saint.name,
    "description": saint.description,
    "birthDate": saint.birthDate,
    "deathDate": saint.deathDate,
    "birthPlace": saint.birthPlace,
    "nationality": saint.nationality,
    "religion": "Christianity",
    "denomination": "Catholic",
    "canonizationDate": saint.canonizationDate,
    "feastDay": saint.feastDay,
    "patronOf": saint.patronOf,
    "image": `${url}${saint.image || image}`,
    "sameAs": saint.externalLinks || [],
    "knowsAbout": saint.attributes || [],
    "worksFor": {
      "@type": "ReligiousOrganization",
      "name": "Catholic Church"
    },
    "additionalType": "Saint",
    "mainEntityOfPage": {
      "@type": "WebPage",
      "@id": url
    }
  } : null;

  // Schema para orações
  const prayerSchema = prayer ? {
    "@context": "https://schema.org",
    "@type": "CreativeWork",
    "name": prayer.title,
    "description": prayer.description,
    "text": prayer.text,
    "inLanguage": "pt-BR",
    "genre": "Prayer",
    "author": prayer.author || "Catholic Tradition",
    "publisher": {
      "@type": "Organization",
      "name": siteTitle
    },
    "dateCreated": prayer.dateCreated,
    "audience": {
      "@type": "Audience",
      "audienceType": "Catholics"
    },
    "about": {
      "@type": "Thing",
      "name": "Catholic Prayer",
      "description": "Traditional Catholic prayer for spiritual devotion"
    },
    "isPartOf": {
      "@type": "Collection",
      "name": "Catholic Prayers Collection"
    }
  } : null;

  // FAQ Schema (se aplicável)
  const faqSchema = article?.faqs ? {
    "@context": "https://schema.org",
    "@type": "FAQPage",
    "mainEntity": article.faqs.map(faq => ({
      "@type": "Question",
      "name": faq.question,
      "acceptedAnswer": {
        "@type": "Answer",
        "text": faq.answer
      }
    }))
  } : null;

  // Combinar todos os schemas
  const allSchemas = [
    websiteSchema,
    religiousOrganizationSchema,
    breadcrumbSchema,
    articleSchema,
    saintSchema,
    prayerSchema,
    faqSchema,
    structuredData
  ].filter(Boolean);

  // Meta tags específicas para conteúdo religioso
  const religiousMetaTags = () => {
    if (saint) {
      return [
        <meta key="saint-name" name="saint.name" content={saint.name} />,
        <meta key="saint-feast" name="saint.feastDay" content={saint.feastDay} />,
        <meta key="saint-patron" name="saint.patronOf" content={saint.patronOf?.join(', ')} />
      ];
    }
    if (prayer) {
      return [
        <meta key="prayer-type" name="prayer.type" content={prayer.type} />,
        <meta key="prayer-occasion" name="prayer.occasion" content={prayer.occasion} />
      ];
    }
    return [];
  };

  return (
    <Helmet>
      {/* Basic Meta Tags */}
      <title>{fullTitle}</title>
      <meta name="description" content={description} />
      <meta name="keywords" content={keywords} />
      <meta name="author" content={author} />
      
      {/* Open Graph / Facebook */}
      <meta property="og:type" content={type} />
      <meta property="og:title" content={fullTitle} />
      <meta property="og:description" content={description} />
      <meta property="og:image" content={`${url}${image}`} />
      <meta property="og:image:width" content="1200" />
      <meta property="og:image:height" content="630" />
      <meta property="og:url" content={url} />
      <meta property="og:site_name" content={siteTitle} />
      <meta property="og:locale" content="pt_BR" />
      
      {/* Article specific OG tags */}
      {article && (
        <>
          <meta property="article:published_time" content={publishedTime} />
          <meta property="article:modified_time" content={modifiedTime || publishedTime} />
          <meta property="article:author" content={article.author || author} />
          <meta property="article:section" content={category} />
          {tags.map(tag => (
            <meta key={tag} property="article:tag" content={tag} />
          ))}
        </>
      )}
      
      {/* Twitter Cards */}
      <meta name="twitter:card" content="summary_large_image" />
      <meta name="twitter:title" content={fullTitle} />
      <meta name="twitter:description" content={description} />
      <meta name="twitter:image" content={`${url}${image}`} />
      <meta name="twitter:creator" content="@apenasumcatolico" />
      
      {/* Additional SEO Tags */}
      <meta name="robots" content="index, follow, max-snippet:-1, max-image-preview:large, max-video-preview:-1" />
      <meta name="language" content="pt-BR" />
      <meta name="revisit-after" content="7 days" />
      <meta name="distribution" content="global" />
      <meta name="rating" content="general" />
      <meta name="subject" content="Religion, Catholicism, Saints, Prayers" />
      <meta name="classification" content="Religion" />
      <meta name="category" content="Religion" />
      
      {/* Geographic targeting */}
      <meta name="geo.region" content="BR" />
      <meta name="geo.country" content="Brazil" />
      
      {/* Religious specific meta tags */}
      {religiousMetaTags()}
      
      {/* Canonical URL */}
      <link rel="canonical" href={url} />
      
      {/* Alternative language versions */}
      <link rel="alternate" href={url} hrefLang="pt-br" />
      <link rel="alternate" href={url} hrefLang="pt" />
      
      {/* DNS prefetch for performance */}
      <link rel="dns-prefetch" href="//fonts.googleapis.com" />
      <link rel="dns-prefetch" href="//www.google-analytics.com" />
      
      {/* Structured Data */}
      {allSchemas.map((schema, index) => (
        <script key={index} type="application/ld+json">
          {JSON.stringify(schema)}
        </script>
      ))}
    </Helmet>
  );
};

// Componente para Breadcrumbs visuais
export const Breadcrumbs = ({ items, className }) => {
  if (!items || items.length === 0) return null;

  return (
    <nav 
      className={className}
      aria-label="Navegação estrutural"
      role="navigation"
    >
      <ol style={{ 
        display: 'flex', 
        listStyle: 'none', 
        padding: 0, 
        margin: 0,
        flexWrap: 'wrap'
      }}>
        {items.map((item, index) => (
          <li key={index} style={{ display: 'flex', alignItems: 'center' }}>
            {index > 0 && (
              <span 
                style={{ margin: '0 8px', color: '#666' }}
                aria-hidden="true"
              >
                /
              </span>
            )}
            {index === items.length - 1 ? (
              <span 
                style={{ color: '#666' }}
                aria-current="page"
              >
                {item.name}
              </span>
            ) : (
              <a 
                href={item.url}
                style={{ 
                  color: '#1B3358', 
                  textDecoration: 'none',
                  padding: '4px 8px',
                  borderRadius: '4px',
                  transition: 'background-color 0.2s'
                }}
                onMouseOver={e => e.target.style.backgroundColor = '#f0f0f0'}
                onMouseOut={e => e.target.style.backgroundColor = 'transparent'}
              >
                {item.name}
              </a>
            )}
          </li>
        ))}
      </ol>
    </nav>
  );
};

export default SEO;
