import { createGlobalStyle } from 'styled-components';

const breakpoints = {
  mobile: '320px',
  tablet: '768px',
  desktop: '1024px',
  large: '1440px'
};

const fonts = {
  heading: "'Inter', sans-serif",
  body: "'Inter', sans-serif",
  sizes: {
    xs: '0.75rem',
    sm: '0.875rem',
    md: '1rem',
    lg: '1.125rem',
    xl: '1.25rem',
