    medium: '0 4px 12px rgba(0, 0, 0, 0.4)',
  },
  breakpoints,
  fonts,
  spacing,
  transitions
};

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html, body {
    width: 100%;
    max-width: 100vw;
