    position: relative;
  }

  #root {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    overflow-x: hidden;
  }

  main {
    flex: 1;
    width: 100%;
    max-width: 100%;
    overflow-x: hidden;
    padding-top: 175px; /* Ajustado para o header fixo */

    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
