import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import OptimizedImage from '../../components/OptimizedImage';

const Section = styled.section`
  padding: ${props => props.theme.spacing.xxl} 0;
  max-width: 1200px;
  margin: 0 auto;
  padding-left: ${props => props.theme.spacing.lg};
  padding-right: ${props => props.theme.spacing.lg};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: ${props => props.theme.spacing.xl} ${props => props.theme.spacing.md};
  }
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  font-size: clamp(2rem, 5vw, 3rem);
  color: ${props => props.theme.colors.titleColor};
  position: relative;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.5rem;
    margin-bottom: ${props => props.theme.spacing.lg};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const SantosGrid = styled(motion.div)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  margin-top: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: ${props => props.theme.spacing.lg};
  }

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.md};
  }
`;

const SantoCard = styled(motion.div)`
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  overflow: hidden;
  box-shadow: ${props => props.theme.shadows.light};
  transition: all ${props => props.theme.transitions.normal};
  cursor: ${props => props.isClickable ? 'pointer' : 'default'};

  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.medium};
  }

  a {
    text-decoration: none;
    color: inherit;
    display: block;
  }

  .image-container {
    position: relative;
    height: 280px;
    overflow: hidden;
    
    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      height: 250px;
    }
  }
`;

const SantoInfo = styled.div`
  padding: ${props => props.theme.spacing.lg};
  text-align: center;

  h3 {
    color: ${props => props.theme.colors.titleColor};
    margin-bottom: ${props => props.theme.spacing.sm};
    font-size: 1.25rem;
  }

  p {
    color: ${props => props.theme.colors.textLight};
    font-size: 0.9rem;
    line-height: 1.6;
  }
`;

const santos = [
  {
    id: 4,
    name: 'São Francisco de Assis',
    image: '/images/santos/São franciso.jpg',
    description: 'Padroeiro dos animais e da ecologia',
    link: '/santos/sao-francisco',
    objectPosition: 'center 20%'
  },
  {
    id: 2,
    name: 'Santa Teresinha',
    image: '/images/santos/Santa terezinha.jpg',
    description: 'Doutora da Igreja e padroeira das missões',
    objectPosition: 'center 30%'
  },
  {
    id: 3,
    name: 'São Padre Pio',
    image: '/images/santos/Padre-Pio.webp',
    description: 'Padre estigmatizado e confessor',
    objectPosition: 'center 25%'
  },
  {
    id: 1,
    name: 'Nossa Senhora',
    image: '/images/santos/Nossa senhora.webp',
    description: 'Mãe de Jesus e nossa intercessora',
    objectPosition: 'center 40%'
  }
];

const SantosSection = () => {
  return (
    <Section>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        História dos Santos
      </SectionTitle>

      <SantosGrid
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ duration: 0.6, delay: 0.2 }}
        viewport={{ once: true }}
      >
        {santos.map((santo, index) => (
          <SantoCard
            key={santo.id}
            isClickable={!!santo.link}
            whileHover={{ y: -10 }}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: index * 0.1 }}
            viewport={{ once: true }}
          >
            {santo.link ? (
              <Link to={santo.link}>
                <div className="image-container">
                  <OptimizedImage 
                    src={santo.image} 
                    alt={santo.name} 
                    height="100%"
                    hover
                    objectPosition={santo.objectPosition}
                  />
                </div>
                <SantoInfo>
                  <h3>{santo.name}</h3>
                  <p>{santo.description}</p>
                </SantoInfo>
              </Link>
            ) : (
              <>
                <div className="image-container">
                  <OptimizedImage 
                    src={santo.image} 
                    alt={santo.name} 
                    height="100%"
                    hover
                    objectPosition={santo.objectPosition}
                  />
                </div>
                <SantoInfo>
                  <h3>{santo.name}</h3>
                  <p>{santo.description}</p>
                </SantoInfo>
              </>
            )}
          </SantoCard>
        ))}
      </SantosGrid>
    </Section>
  );
};

export default SantosSection;
