import React from 'react';
import styled from 'styled-components';
import BaseHero, { BaseHeroSection, BaseHeroContent, BaseHeroText } from './BaseHero';
import { desktopStyles } from '../../styles/theme';

const DesktopHeroSection = styled(BaseHeroSection)`
  height: 80vh;
  min-height: 600px;
`;

const DesktopHeroContent = styled(BaseHeroContent)`
  padding: ${desktopStyles.spacing.xxl};
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  justify-content: center;
  height: 100%;

  h1 {
    font-size: ${desktopStyles.typography.hero.title};
    margin-bottom: ${desktopStyles.spacing.lg};
  }

  p {
    font-size: ${desktopStyles.typography.hero.subtitle};
    max-width: 800px;
    margin: 0 auto;
    line-height: 1.6;
  }
`;

const DesktopHero = ({ title, subtitle }) => {
  return (
    <DesktopHeroSection>
      <DesktopHeroContent>
        <BaseHeroText>
          <h1>{title}</h1>
          <p>{subtitle}</p>
        </BaseHeroText>
      </DesktopHeroContent>
    </DesktopHeroSection>
  );
};

export default DesktopHero;
