import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FiHeart, FiInstagram, FiMail, FiGithub } from 'react-icons/fi';

const FooterContainer = styled.footer`
  background: ${({ theme }) => theme.colors.footerBg};
  color: ${({ theme }) => theme.colors.white};
  padding: ${({ theme }) => theme.spacing.xxl} 0 ${({ theme }) => theme.spacing.lg};
  margin-top: auto;
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 ${({ theme }) => theme.spacing.lg};
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${({ theme }) => theme.spacing.xl};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    text-align: center;
  }
`;

const FooterSection = styled.div`
  h3 {
    color: ${({ theme }) => theme.colors.primary};
    margin-bottom: ${({ theme }) => theme.spacing.md};
    font-size: 1.2rem;
  }
  
  p {
    color: ${({ theme }) => theme.colors.textLight};
    line-height: 1.6;
  }
`;

const FooterLinks = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${({ theme }) => theme.spacing.sm};
  
  a {
    color: ${({ theme }) => theme.colors.textLight};
    text-decoration: none;
    transition: color ${({ theme }) => theme.transitions.normal};
    
    &:hover {
      color: ${({ theme }) => theme.colors.primary};
    }
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing.md};
  margin-top: ${({ theme }) => theme.spacing.md};
  
  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    justify-content: center;
  }
`;

const SocialLink = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  background: rgba(255, 255, 255, 0.1);
  color: ${({ theme }) => theme.colors.textLight};
  border-radius: 50%;
  text-decoration: none;
  transition: all ${({ theme }) => theme.transitions.normal};
  
  &:hover {
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
    transform: translateY(-2px);
  }
`;

const FooterBottom = styled.div`
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  margin-top: ${({ theme }) => theme.spacing.xl};
  padding-top: ${({ theme }) => theme.spacing.lg};
  text-align: center;
  color: ${({ theme }) => theme.colors.textLight};
  
  p {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: ${({ theme }) => theme.spacing.sm};
    
    @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
      flex-direction: column;
      gap: ${({ theme }) => theme.spacing.xs};
    }
  }
`;

const HeartIcon = styled(FiHeart)`
  color: #e74c3c;
  animation: pulse 1.5s ease-in-out infinite;
  
  @keyframes pulse {
    0%, 100% {
      transform: scale(1);
    }
    50% {
      transform: scale(1.2);
    }
  }
`;

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <FooterContainer>
      <FooterContent>
        <FooterSection>
          <h3>Sobre</h3>
          <p>
            Site católico dedicado à evangelização e devoção aos santos. 
            Nossa missão é compartilhar a fé e inspirar através das histórias 
            dos santos e reflexões espirituais.
          </p>
          <SocialLinks>
            <SocialLink 
              href="https://www.instagram.com/joaocarlostdm/?hl=pt-br" 
              target="_blank" 
              rel="noopener noreferrer"
              aria-label="Instagram"
            >
              <FiInstagram />
            </SocialLink>
            <SocialLink 
              href="mailto:contato@apenasumcatolico.com" 
              aria-label="Email"
            >
              <FiMail />
            </SocialLink>
            <SocialLink 
              href="https://github.com/seu-usuario/apenas-um-catolico" 
              target="_blank" 
              rel="noopener noreferrer"
              aria-label="GitHub"
            >
              <FiGithub />
            </SocialLink>
          </SocialLinks>
        </FooterSection>

        <FooterSection>
          <h3>Navegação</h3>
          <FooterLinks>
            <Link to="/">Início</Link>
            <Link to="/santos">Santos</Link>
            <Link to="/oracao">Orações</Link>
            <Link to="/calendario">Calendário</Link>
            <Link to="/blog">Blog</Link>
            <Link to="/sobre">Sobre</Link>
          </FooterLinks>
        </FooterSection>

        <FooterSection>
          <h3>Recursos</h3>
          <FooterLinks>
            <Link to="/oracao">Orações Diárias</Link>
            <Link to="/calendario">Calendário Litúrgico</Link>
            <Link to="/blog">Reflexões</Link>
            <Link to="/santos">Histórias dos Santos</Link>
            <a href="https://www.bibliaonline.com.br" target="_blank" rel="noopener noreferrer">
              Bíblia Online
            </a>
            <a href="https://www.vatican.va" target="_blank" rel="noopener noreferrer">
              Vaticano
            </a>
          </FooterLinks>
        </FooterSection>

        <FooterSection>
          <h3>Contato</h3>
          <p>
            Tem alguma sugestão ou quer compartilhar uma experiência? 
            Entre em contato conosco através das nossas redes sociais 
            ou envie um email.
          </p>
          <p>
            Que Deus abençoe você e sua família!
          </p>
        </FooterSection>
      </FooterContent>

      <FooterBottom>
        <p>
          &copy; {currentYear} - Apenas um Católico
          <span>•</span>
          Feito com <HeartIcon /> para evangelizar e inspirar
          <span>•</span>
          Todos os direitos reservados
        </p>
      </FooterBottom>
    </FooterContainer>
  );
};

export default Footer;
