import React from 'react';
import styled from 'styled-components';
import useDeviceType from '../components/useDeviceType';
import SEO from '../components/SEO';
import HeroSection from './Home/HeroSection';
import BiblicalSection from './Home/BiblicalSection';
import SantosSection from './Home/SantosSection';
import ResourcesSection from './Home/ResourcesSection';

const HomeContainer = styled.div`
  min-height: 100vh;
`;

const ContentSection = styled.section`
  padding: ${props => props.isMobile ? '20px 0' : '40px 0'};
  max-width: 1200px;
  margin: 0 auto;
`;

const Home = () => {
  const { isMobile } = useDeviceType();

  return (
    <HomeContainer>
      <SEO
        title="Início"
        description="Bem-vindo ao site católico dedicado à evangelização e devoção aos santos. Jesus: Nosso Senhor e Salvador."
        keywords="católico, santos, orações, evangelização, fé, devoção, Jesus Cristo, início"
        url="https://apenas-um-catolico.vercel.app/"
      />

      <HeroSection />

      <ContentSection isMobile={isMobile}>
        <BiblicalSection />
        <SantosSection />
        <ResourcesSection />
      </ContentSection>
    </HomeContainer>
  );
};

export default Home;
