import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus } from 'react-icons/fi';
import { Card } from '../components/Card';

const LiturgiaContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 40px 20px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 10px;
  }
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const LoadingSpinner = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.theme.colors.background};
  border-top: 3px solid ${props => props.theme.colors.secondary};
  border-radius: 50%;
  margin: 20px auto;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: ${props => props.theme.colors.error};
  padding: 20px;
  background: ${props => props.theme.colors.background};
  border-radius: 8px;
  margin: 20px 0;
`;

const LiturgiaCard = styled(Card)`
  h2 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: 16px;
    font-size: 1.5rem;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 0.9rem;
      margin-bottom: 6px;
    }
  }

  p {
    color: ${props => props.theme.colors.text};
    line-height: 1.8;
    margin-bottom: 16px;
    font-size: ${props => props.fontSize}px;

    strong {
      color: ${props => props.theme.colors.primary};
      font-weight: 700;
    }

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      line-height: 1.4;
      margin-bottom: 6px;
      font-size: ${props => Math.max(props.fontSize * 0.4, 10)}px;
    }
  }

  .referencia {
    color: ${props => props.theme.colors.textLight};
    font-style: italic;
    font-size: ${props => Math.max(props.fontSize - 2, 12)}px;
    text-align: right;

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: ${props => Math.max((props.fontSize - 2) * 0.4, 8)}px;
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    transform: scale(0.6);
    transform-origin: top center;
  }
`;

const DataLiturgia = styled.div`
  text-align: center;
  margin-bottom: 24px;
  color: ${props => props.theme.colors.textLight};
  font-size: 1.1rem;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    margin-bottom: 10px;
    font-size: 0.66rem;
  }
`;

const FontControls = styled.div`
  display: flex;
  justify-content: center;
  gap: 12px;
  margin-bottom: 24px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 5px;
    margin-bottom: 10px;
  }
`;

const FontButton = styled.button`
  background: ${props => props.theme.colors.cardBackground};
  border: 1px solid ${props => props.theme.colors.borderColor};
  color: ${props => props.theme.colors.text};
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: ${props => props.theme.colors.white};
    border-color: ${props => props.theme.colors.primary};
  }

  svg {
    width: 20px;
    height: 20px;
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    width: 16px;
    height: 16px;

    svg {
      width: 8px;
      height: 8px;
    }
  }
`;

const formatarTextoComNumeros = (texto) => {
  if (!texto) return '';
  return texto.replace(/(\d+)/g, '<strong>$1</strong>');
};

const LiturgiaDiaria = () => {
  const [liturgia, setLiturgia] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [fontSize, setFontSize] = useState(16);

  useEffect(() => {
    const fetchLiturgia = async () => {
      try {
        const response = await fetch('https://liturgia.up.railway.app/');
        if (!response.ok) {
          throw new Error('Erro ao carregar a liturgia');
        }
        const data = await response.json();
        console.log('Dados da API:', data);
        setLiturgia(data);
        setLoading(false);
      } catch (err) {
        console.error('Erro ao buscar liturgia:', err);
        setError('Não foi possível carregar a liturgia. Por favor, tente novamente mais tarde.');
        setLoading(false);
      }
    };

    fetchLiturgia();
  }, []);

  const handleFontSizeChange = (delta) => {
    setFontSize(prevSize => {
      const newSize = prevSize + delta;
      return Math.min(Math.max(newSize, 12), 24); // Limita entre 12 e 24
    });
  };

  if (loading) {
    return (
      <LiturgiaContainer>
        <Title>Liturgia Diária</Title>
        <LoadingSpinner
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
      </LiturgiaContainer>
    );
  }

  if (error) {
    return (
      <LiturgiaContainer>
        <Title>Liturgia Diária</Title>
        <ErrorMessage>{error}</ErrorMessage>
      </LiturgiaContainer>
    );
  }

  const leituras = [
    { titulo: 'Primeira Leitura', texto: liturgia?.primeiraLeitura?.texto, referencia: liturgia?.primeiraLeitura?.referencia },
    { titulo: 'Salmo Responsorial', texto: liturgia?.salmo?.texto, referencia: liturgia?.salmo?.referencia },
    { titulo: 'Segunda Leitura', texto: liturgia?.segundaLeitura?.texto, referencia: liturgia?.segundaLeitura?.referencia },
    { titulo: 'Evangelho', texto: liturgia?.evangelho?.texto, referencia: liturgia?.evangelho?.referencia }
  ].filter(leitura => leitura.texto && leitura.referencia);

  return (
    <LiturgiaContainer>
      <Helmet>
        <title>Liturgia Diária - Apenas um Católico</title>
        <meta name="description" content="Acompanhe a liturgia diária da Igreja Católica" />
      </Helmet>

      <Title>Liturgia Diária</Title>

      <DataLiturgia>
        {new Date().toLocaleDateString('pt-BR', {
          weekday: 'long',
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        })}
      </DataLiturgia>

      <FontControls>
        <FontButton onClick={() => handleFontSizeChange(-1)} aria-label="Diminuir fonte">
          <FiMinus />
        </FontButton>
        <FontButton onClick={() => handleFontSizeChange(1)} aria-label="Aumentar fonte">
          <FiPlus />
        </FontButton>
      </FontControls>

      {liturgia && leituras.map((leitura, index) => (
        <LiturgiaCard
          key={index}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: index * 0.2 }}
          fontSize={fontSize}
        >
          <h2>{leitura.titulo}</h2>
          <p dangerouslySetInnerHTML={{ __html: formatarTextoComNumeros(leitura.texto) }} />
          <p className="referencia">{leitura.referencia}</p>
        </LiturgiaCard>
      ))}

      {liturgia && leituras.length === 0 && (
        <ErrorMessage>
          Não há leituras disponíveis para hoje. Por favor, tente novamente mais tarde.
        </ErrorMessage>
      )}
    </LiturgiaContainer>
  );
};

export default LiturgiaDiaria;
