import React, { useState } from 'react';
import styled from 'styled-components';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';

const mockLiturgia = {
  data: "19 de Junho de 2025",
  dia_semana: "Sexta-feira da 11ª Semana do Tempo Comum",
  leituras: [
    {
      titulo: "1ª Leitura",
      referencia: "2Cor 11,18.21b-30",
      texto: "Irmãos, 18 já que muitos se gloriam segundo a carne, eu também me gloriarei. 21b O que outros ousam dizer em vantagem própria, eu também o digo a meu respeito, embora fale como insensato. 22 São hebreus? Eu também. São israelitas? Eu também. São da descendência de Abraão? Eu também. 23 São servos de Cristo? Como menos sensato digo: Eu ainda mais..."
    },
    {
      titulo: "Salmo",
      referencia: "Sl 33(34)",
      texto: "O Senhor livra de todas as angústias os seus justos. Bendirei o Senhor Deus em todo o tempo, seu louvor estará sempre em minha boca. Provai e vede quão suave é o Senhor! Feliz o homem que tem nele o seu refúgio."
    },
    {
      titulo: "Evangelho",
      referencia: "Mt 6,19-23",
      texto: "Naquele tempo, disse Jesus aos seus discípulos: 19 'Não junteis tesouros aqui na terra, onde a traça e a ferrugem destroem, e os ladrões assaltam e roubam. 20 Ao contrário, juntai para vós tesouros no céu, onde nem a traça e a ferrugem destroem, nem os ladrões assaltam e roubam. 21 Porque, onde está o teu tesouro, aí estará também o teu coração.'"
    }
  ]
};

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 40px 16px 80px 16px;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 2rem;
`;

const LiturgiaTitle = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 0.5rem;
  color: ${({ theme }) => theme.colors.text};
`;

const ActionsContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const ActionButton = styled.button`
  background: transparent;
  border: 1px solid #ccc;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: ${({ theme }) => theme.colors.textLight};

  &:hover {
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
  }
`;

const TabsContainer = styled.div`
  display: flex;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  margin-bottom: 2rem;
`;

const TabButton = styled.button`
  flex: 1;
  padding: 1rem;
  font-size: 1rem;
  font-weight: 500;
  border: none;
  background: ${({ active, theme }) => (active ? theme.colors.primary : 'transparent')};
  color: ${({ active, theme }) => (active ? theme.colors.white : theme.colors.textLight)};
  cursor: pointer;
  transition: all 0.3s ease;

  &:not(:last-child) {
    border-right: 1px solid #ddd;
  }
`;

const Content = styled.div`
  font-size: ${({ fontSize }) => fontSize}px;
  line-height: 1.8;
  color: ${({ theme }) => theme.colors.textLight};
  
  h2, h3 {
    color: ${({ theme }) => theme.colors.text};
    margin-bottom: 1rem;
  }
`;

const LiturgiaDiaria = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);
  const { dia_semana, leituras } = mockLiturgia;

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Liturgia Diária',
        text: `Liturgia de ${dia_semana}`,
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      alert("Compartilhamento não suportado neste navegador.");
    }
  };

  return (
    <Container>
      <Helmet>
        <title>Liturgia Diária - Apenas um Católico</title>
        <meta name="description" content="Acompanhe a Liturgia Diária da Igreja Católica." />
      </Helmet>
      
      <Header>
        <LiturgiaTitle>{dia_semana}</LiturgiaTitle>
        <ActionsContainer>
          <ActionButton onClick={() => setFontSize(s => Math.max(s - 1, 12))} aria-label="Diminuir fonte">
            <FiMinus />
          </ActionButton>
          <ActionButton onClick={() => setFontSize(s => Math.min(s + 1, 24))} aria-label="Aumentar fonte">
            <FiPlus />
          </ActionButton>
          <ActionButton onClick={handleShare} aria-label="Compartilhar">
            <FiShare2 />
          </ActionButton>
        </ActionsContainer>
      </Header>

      <TabsContainer>
        {leituras.map((leitura, index) => (
          <TabButton 
            key={index} 
            active={activeTab === index} 
            onClick={() => setActiveTab(index)}
          >
            {leitura.titulo}
          </TabButton>
        ))}
      </TabsContainer>

      <Content fontSize={fontSize}>
        <h2>{leituras[activeTab].referencia}</h2>
        <p>{leituras[activeTab].texto}</p>
      </Content>
    </Container>
  );
};

export default LiturgiaDiaria;
