import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';

const LaudesContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  padding: 40px 20px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 10px;
  }
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 32px;
`;

const DateInfo = styled.div`
  color: ${props => props.theme.colors.textLight};
  font-size: 1rem;
  margin-bottom: 8px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.9rem;
  }
`;

const LaudesTitle = styled.h2`
  color: ${props => props.theme.colors.secondary};
  font-size: 1.8rem;
  font-weight: 600;
  margin: 0;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.4rem;
  }
`;

const Subtitle = styled.p`
  color: ${props => props.theme.colors.textLight};
  font-size: 1rem;
  margin-top: 8px;
  font-style: italic;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.9rem;
  }
`;

const TabContainer = styled.div`
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  box-shadow: ${props => props.theme.shadows.medium};
  overflow: hidden;
  margin-bottom: 24px;
`;

const TabHeader = styled.div`
  display: flex;
  background: ${props => props.theme.colors.background};
  border-bottom: 1px solid ${props => props.theme.colors.borderColor};
  overflow-x: auto;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    flex-wrap: wrap;
  }
`;

const TabButton = styled.button`
  flex: 1;
  min-width: 120px;
  padding: 16px 20px;
  background: ${props => props.active ? props.theme.colors.primary : 'transparent'};
  color: ${props => props.active ? props.theme.colors.white : props.theme.colors.text};
  border: none;
  font-size: 0.9rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  white-space: nowrap;

  &:hover {
    background: ${props => props.active ? props.theme.colors.primary : 'rgba(27, 51, 88, 0.1)'};
  }

  &:not(:last-child) {
    border-right: 1px solid ${props => props.theme.colors.borderColor};
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    flex: 1 1 50%;
    min-width: auto;
    padding: 12px 16px;
    font-size: 0.8rem;
    border-right: none;
    border-bottom: 1px solid ${props => props.theme.colors.borderColor};
    
    &:nth-child(odd) {
      border-right: 1px solid ${props => props.theme.colors.borderColor};
    }
  }
`;

const TabContent = styled(motion.div)`
  padding: 32px;
  min-height: 400px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px;
    min-height: 300px;
  }
`;

const SectionTitle = styled.h3`
  color: ${props => props.theme.colors.primary};
  font-size: 1.3rem;
  margin-bottom: 20px;
  font-weight: 600;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.1rem;
    margin-bottom: 16px;
  }
`;

const ContentText = styled.div`
  color: ${props => props.theme.colors.text};
  line-height: 1.8;
  font-size: ${props => props.fontSize}px;
  margin-bottom: 24px;

  p {
    margin-bottom: 16px;
  }

  .antiphon {
    color: ${props => props.theme.colors.secondary};
    font-style: italic;
    font-weight: 500;
    margin-bottom: 12px;
    padding: 8px 12px;
    background: rgba(192, 149, 83, 0.1);
    border-left: 3px solid ${props => props.theme.colors.secondary};
    border-radius: 0 4px 4px 0;
  }

  .psalm-verse {
    margin-bottom: 8px;
    text-indent: 20px;
  }

  .response {
    color: ${props => props.theme.colors.primary};
    font-weight: 600;
    text-align: center;
    margin: 16px 0;
    padding: 8px;
    background: rgba(27, 51, 88, 0.1);
    border-radius: 4px;
  }

  strong {
    color: ${props => props.theme.colors.primary};
    font-weight: 700;
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    line-height: 1.6;
    font-size: ${props => Math.max(props.fontSize * 0.9, 14)}px;
  }
`;

const ContentReference = styled.div`
  color: ${props => props.theme.colors.textLight};
  font-style: italic;
  font-size: ${props => Math.max(props.fontSize - 2, 12)}px;
  text-align: right;
  padding-top: 16px;
  border-top: 1px solid ${props => props.theme.colors.borderColor};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: ${props => Math.max((props.fontSize - 2) * 0.9, 11)}px;
  }
`;

const Controls = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
  padding: 16px;
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 8px;
  box-shadow: ${props => props.theme.shadows.light};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    flex-direction: column;
    gap: 12px;
    padding: 12px;
  }
`;

const FontControls = styled.div`
  display: flex;
  gap: 8px;
  align-items: center;
`;

const FontLabel = styled.span`
  color: ${props => props.theme.colors.textLight};
  font-size: 0.9rem;
  margin-right: 8px;
`;

const FontButton = styled.button`
  background: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.borderColor};
  color: ${props => props.theme.colors.text};
  width: 36px;
  height: 36px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.theme.colors.primary};
    color: ${props => props.theme.colors.white};
    border-color: ${props => props.theme.colors.primary};
  }

  svg {
    width: 16px;
    height: 16px;
  }
`;

const ShareButton = styled.button`
  background: ${props => props.theme.colors.secondary};
  color: ${props => props.theme.colors.white};
  border: none;
  padding: 8px 16px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.9rem;

  &:hover {
    background: ${props => props.theme.colors.primary};
    transform: translateY(-1px);
  }

  svg {
    width: 16px;
    height: 16px;
  }
`;

const LoadingSpinner = styled(motion.div)`
  width: 50px;
  height: 50px;
  border: 3px solid ${props => props.theme.colors.background};
  border-top: 3px solid ${props => props.theme.colors.secondary};
  border-radius: 50%;
  margin: 40px auto;
`;

const ErrorMessage = styled.div`
  text-align: center;
  color: ${props => props.theme.colors.error};
  padding: 32px;
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  margin: 20px 0;
  box-shadow: ${props => props.theme.shadows.light};
`;

const Laudes = () => {
  const [laudes, setLaudes] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);

  useEffect(() => {
    const fetchLaudes = async () => {
      try {
        // Dados completos das Laudes para demonstração
        const mockLaudes = {
          invitatorium: {
            ant: "Vinde, adoremos o Senhor, nosso Rei e Salvador.",
            psalm: `Vinde, exultemos de alegria no Senhor,
            aclamemos o Rochedo que nos salva!
            Apresentemo-nos a ele com louvores
            e com cantos o nosso júbilo expressemos!

            Porque Deus é o grande Senhor,
            é o grande Rei de toda a criação;
            em suas mãos se encontram os abismos,
            e são suas as montanhas e colinas.

            Dele é o mar, pois foi ele quem o fez,
            a terra firme que suas mãos moldaram.
            Vinde, prostrados, nós o adoremos,
            e ajoelhemos ante Deus, que nos criou!`
          },
          hymn: `Ó esplendor da glória do Pai,
          ó luz da luz e fonte de luz,
          ó dia que iluminas o dia,
          ó sol que brilhas no meio-dia.

          Vem, sol verdadeiro,
          com teu fulgor eterno,
          e derrama nos nossos corações
          a luz do Espírito Santo.

          Cantemos ao Pai onipotente,
          cantemos ao Filho Redentor,
          e ao Espírito Consolador
          louvores na eternidade. Amém.`,
          psalms: [
            {
              ant: "Cantai ao Senhor um cântico novo, porque Ele fez maravilhas.",
              psalm: `Minha alma tem sede de Deus,
              do Deus vivo:
              quando irei contemplar
              a face de Deus?

              Como a corça suspira
              pelas águas correntes,
              assim minha alma suspira
              por vós, ó meu Deus.

              Por que estás triste, ó minha alma,
              por que te perturbas?
              Espera em Deus! Hei de louvá-lo ainda,
              meu Salvador e meu Deus!`,
              title: "Salmo 63 (62) - Sede de Deus"
            },
            {
              ant: "O Senhor é minha luz e salvação.",
              psalm: `Bendizei, ó minha alma, ao Senhor,
              e todo o meu ser, seu santo nome!
              Bendizei, ó minha alma, ao Senhor,
              não esqueçais nenhum de seus favores!

              É ele quem perdoa toda culpa,
              e cura toda a vossa enfermidade;
              é ele quem da morte vos liberta,
              e vos cumula de amor e compaixão.`,
              title: "Cântico de Ezequias (Is 38, 10-14.17-20)"
            }
          ],
          lectioBrevis: {
            reading: `Irmãos, tudo o que é verdadeiro, tudo o que é nobre, tudo o que é justo, tudo o que é puro, tudo o que é amável, tudo o que é de boa fama, se alguma virtude há e se algum louvor existe, seja isso o que ocupe os vossos pensamentos.`,
            reference: "Fl 4, 8"
          },
          benedictus: {
            ant: "Bendito seja o Senhor Deus de Israel, porque visitou e redimiu o seu povo.",
            psalm: `Bendito seja o Senhor Deus de Israel,
            porque visitou e redimiu o seu povo,
            e nos suscitou uma força salvadora
            na casa de Davi, seu servo,

            como havia prometido pela boca dos santos,
            seus profetas, desde os tempos antigos:
            salvação contra os nossos inimigos
            e contra a mão de todos os que nos odeiam.

            E a ti, menino, chamarão profeta do Altíssimo,
            porque irás à frente do Senhor preparar os seus caminhos,
            para dar ao seu povo o conhecimento da salvação
            pelo perdão dos seus pecados.`
          },
          preces: [
            "Cristo, sol de justiça, que iluminas todo homem que vem a este mundo, nós vos louvamos.",
            "Vós que ressuscitastes glorioso no primeiro dia da semana, renovai hoje a nossa alegria.",
            "Vós que aparecestes aos discípulos e lhes dissestes: 'A paz esteja convosco', dai-nos a vossa paz.",
            "Vós que mandastes os Apóstolos anunciar o Evangelho, tornai-nos hoje vossas testemunhas."
          ],
          oratio: `Ó Deus, que fizestes brilhar sobre nós a luz do novo dia, concedei-nos passar este dia sem cair em pecado, para que, ao chegar à tarde, vos possamos louvar com alegria. Por nosso Senhor Jesus Cristo, vosso Filho, na unidade do Espírito Santo. Amém.`
        };
        
        setLaudes(mockLaudes);
        setLoading(false);
      } catch (err) {
        setError('Não foi possível carregar as Laudes. Por favor, tente novamente mais tarde.');
        setLoading(false);
      }
    };

    // Simular um pequeno delay de carregamento
    setTimeout(() => {
      fetchLaudes();
    }, 1000);
  }, []);

  const handleFontSizeChange = (delta) => {
    setFontSize(prevSize => {
      const newSize = prevSize + delta;
      return Math.min(Math.max(newSize, 12), 24);
    });
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Laudes - Oração da Manhã',
        text: 'Reze as Laudes de hoje',
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Link copiado para a área de transferência!');
    }
  };

  if (loading) {
    return (
      <LaudesContainer>
        <Header>
          <DateInfo>Carregando...</DateInfo>
          <LaudesTitle>Laudes</LaudesTitle>
          <Subtitle>Oração da Manhã</Subtitle>
        </Header>
        <LoadingSpinner
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
        />
      </LaudesContainer>
    );
  }

  if (error) {
    return (
      <LaudesContainer>
        <Header>
          <DateInfo>
            {new Date().toLocaleDateString('pt-BR', {
              weekday: 'long',
              year: 'numeric',
              month: 'long',
              day: 'numeric'
            })}
          </DateInfo>
          <LaudesTitle>Laudes</LaudesTitle>
          <Subtitle>Oração da Manhã</Subtitle>
        </Header>
        <ErrorMessage>{error}</ErrorMessage>
      </LaudesContainer>
    );
  }

  // Preparar as abas das Laudes
  const tabs = [
    {
      title: 'Invitátorio',
      content: {
        title: 'Invitátorio',
        text: `<div class="antiphon">Ant. ${laudes?.invitatorium?.ant}</div>
               <div class="psalm-verse">${laudes?.invitatorium?.psalm?.replace(/\n/g, '<br/>')}</div>`,
        reference: 'Salmo 95 (94)'
      }
    },
    {
      title: 'Hino',
      content: {
        title: 'Hino',
        text: laudes?.hymn?.replace(/\n/g, '<br/>'),
        reference: ''
      }
    }
  ];

  // Adicionar salmos
  if (laudes?.psalms) {
    laudes.psalms.forEach((psalm, index) => {
      tabs.push({
        title: `Salmo ${index + 1}`,
        content: {
          title: psalm.title || `Salmo ${index + 1}`,
          text: `<div class="antiphon">Ant. ${psalm.ant}</div>
                 <div class="psalm-verse">${psalm.psalm?.replace(/\n/g, '<br/>')}</div>`,
          reference: ''
        }
      });
    });
  }

  // Adicionar outras partes
  if (laudes?.lectioBrevis) {
    tabs.push({
      title: 'Leitura Breve',
      content: {
        title: 'Leitura Breve',
        text: laudes.lectioBrevis.reading,
        reference: laudes.lectioBrevis.reference
      }
    });
  }

  if (laudes?.benedictus) {
    tabs.push({
      title: 'Benedictus',
      content: {
        title: 'Cântico de Zacarias',
        text: `<div class="antiphon">Ant. ${laudes.benedictus.ant}</div>
               <div class="psalm-verse">${laudes.benedictus.psalm?.replace(/\n/g, '<br/>')}</div>`,
        reference: 'Lc 1, 68-79'
      }
    });
  }

  if (laudes?.preces && laudes.preces.length > 0) {
    tabs.push({
      title: 'Preces',
      content: {
        title: 'Preces',
        text: `<div class="response">R. Senhor, tende piedade de nós.</div>
               ${laudes.preces.map(prece => `<p>— ${prece}</p>`).join('')}`,
        reference: ''
      }
    });
  }

  if (laudes?.oratio) {
    tabs.push({
      title: 'Oração',
      content: {
        title: 'Oração Conclusiva',
        text: laudes.oratio,
        reference: ''
      }
    });
  }

  return (
    <LaudesContainer>
      <Helmet>
        <title>Laudes - Apenas um Católico</title>
        <meta name="description" content="Reze as Laudes, a oração da manhã da Liturgia das Horas" />
      </Helmet>

      <Header>
        <DateInfo>
          {new Date().toLocaleDateString('pt-BR', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          })}
        </DateInfo>
        <LaudesTitle>Laudes</LaudesTitle>
        <Subtitle>Oração da Manhã - Liturgia das Horas</Subtitle>
      </Header>

      <Controls>
        <FontControls>
          <FontLabel>Tamanho da fonte:</FontLabel>
          <FontButton onClick={() => handleFontSizeChange(-1)} aria-label="Diminuir fonte">
            <FiMinus />
          </FontButton>
          <FontButton onClick={() => handleFontSizeChange(1)} aria-label="Aumentar fonte">
            <FiPlus />
          </FontButton>
        </FontControls>
        <ShareButton onClick={handleShare}>
          <FiShare2 />
          Compartilhar
        </ShareButton>
      </Controls>

      {tabs.length > 0 && (
        <TabContainer>
          <TabHeader>
            {tabs.map((tab, index) => (
              <TabButton
                key={index}
                active={activeTab === index}
                onClick={() => setActiveTab(index)}
              >
                {tab.title}
              </TabButton>
            ))}
          </TabHeader>

          <TabContent
            key={activeTab}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <SectionTitle>{tabs[activeTab]?.content.title}</SectionTitle>
            <ContentText 
              fontSize={fontSize}
              dangerouslySetInnerHTML={{ 
                __html: tabs[activeTab]?.content.text || '' 
              }} 
            />
            {tabs[activeTab]?.content.reference && (
              <ContentReference fontSize={fontSize}>
                {tabs[activeTab]?.content.reference}
              </ContentReference>
            )}
          </TabContent>
        </TabContainer>
      )}

      {tabs.length === 0 && (
        <ErrorMessage>
          Não há Laudes disponíveis para hoje. Por favor, tente novamente mais tarde.
        </ErrorMessage>
      )}
    </LaudesContainer>
  );
};

export default Laudes;
