import React from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { Helmet } from 'react-helmet';
import { motion } from 'framer-motion';
import { useTheme } from 'styled-components';

const ParchmentGlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=EB+Garamond:ital,wght@0,400;0,700;1,400&display=swap');
`;

const ParchmentContainer = styled(motion.div)`
  background: ${({ theme }) => theme.isDark 
    ? theme.colors.background
    : 'url(https://www.myfreetextures.com/wp-content/uploads/2014/11/old-and-worn-out-parchment-paper.jpg) repeat-y'};
  background-size: 100% auto;
  padding: 60px 20px;
  font-family: 'EB Garamond', serif;
  color: ${({ theme }) => theme.isDark ? theme.colors.text : '#4f4239'};
  min-height: 100vh;
`;

const ContentWrapper = styled.div`
  max-width: 800px;
  margin: 0 auto;
  background: ${({ theme }) => theme.isDark 
    ? 'rgba(30, 30, 30, 0.95)' 
    : 'rgba(255, 255, 255, 0.4)'};
  padding: 40px;
  border-radius: 5px;
  box-shadow: ${({ theme }) => theme.isDark 
    ? '0 0 20px rgba(0,0,0,0.6)' 
    : '0 0 20px rgba(0,0,0,0.2)'};
  border: 1px solid ${({ theme }) => theme.isDark 
    ? '#E5C697' 
    : '#c8bca9'};
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 2rem;
  font-size: 3.5rem;
  font-weight: 700;
  color: ${({ theme }) => theme.isDark ? '#E5C697' : '#6a5a4a'};
  text-shadow: ${({ theme }) => theme.isDark ? '2px 2px 4px rgba(0,0,0,0.3)' : 'none'};
`;

const Section = styled.section`
  margin-bottom: 2.5rem;
  line-height: 1.8;
  font-size: 1.2rem;

  h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    padding-bottom: 0.5rem;
    border-bottom: 2px solid ${({ theme }) => theme.isDark 
      ? '#E5C697' 
      : '#a48f78'};
    color: ${({ theme }) => theme.isDark ? '#E5C697' : '#6a5a4a'};
    text-shadow: ${({ theme }) => theme.isDark ? '2px 2px 4px rgba(0,0,0,0.3)' : 'none'};
  }
  
  h3 {
    font-size: 1.8rem;
    margin-top: 2rem;
    margin-bottom: 1rem;
    color: ${({ theme }) => theme.isDark ? '#E5C697' : '#6a5a4a'};
