import React, { useState, useEffect, useRef, memo, useCallback } from 'react';
import styled from 'styled-components';

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: ${props => props.height || 'auto'};
  overflow: hidden;
  border-radius: ${props => props.borderRadius || '8px'};
  background-color: ${props => props.theme.colors.borderColor};
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: ${props => props.objectFit || 'cover'};
  object-position: ${props => props.objectPosition || 'center'};
  transition: all 0.3s ease;
  opacity: ${props => props.loaded ? 1 : 0};
  transform: ${props => props.loaded ? 'scale(1)' : 'scale(1.02)'};
`;

const PlaceholderContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(90deg, 
    ${props => props.theme.colors.borderColor} 25%, 
    ${props => props.theme.colors.background} 50%, 
    ${props => props.theme.colors.borderColor} 75%
  );
  background-size: 200% 100%;
  animation: ${props => props.show ? 'loading-shimmer 2s infinite' : 'none'};
  opacity: ${props => props.show ? 1 : 0};
  transition: opacity 0.3s ease;

  @keyframes loading-shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }
`;

const PlaceholderIcon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: ${props => props.theme.colors.textLight};
  opacity: 0.3;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &::before {
    content: 'ðŸ“·';
    font-size: 20px;
  }
`;

const ErrorContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.textLight};
  font-size: 0.9rem;
  text-align: center;
  padding: 1rem;
`;

// Hook para Intersection Observer
const useIntersectionObserver = (options = {}) => {
  const [isIntersecting, setIsIntersecting] = useState(false);
  const [hasIntersected, setHasIntersected] = useState(false);
  const elementRef = useRef();

  useEffect(() => {
    const element = elementRef.current;
    if (!element) return;

    const observer = new IntersectionObserver(([entry]) => {
      setIsIntersecting(entry.isIntersecting);
      if (entry.isIntersecting && !hasIntersected) {
        setHasIntersected(true);
      }
    }, {
      threshold: 0.1,
      rootMargin: '50px',
      ...options
    });

    observer.observe(element);

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, [hasIntersected, options]);

  return [elementRef, isIntersecting, hasIntersected];
};

const OptimizedImage = memo(({
  src,
  alt,
  width,
  height,
  objectFit = 'cover',
  objectPosition = 'center',
  borderRadius = '8px',
  placeholder = true,
  lazy = true,
  onLoad,
  onError,
  className,
  style,
  sizes,
  srcSet,
  ...props
}) => {
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(false);
  const [imageRef, isIntersecting, hasIntersected] = useIntersectionObserver();

  // Determinar se deve carregar a imagem
  const shouldLoad = lazy ? hasIntersected : true;

  const handleLoad = useCallback((e) => {
    setLoaded(true);
    setError(false);
    onLoad?.(e);
  }, [onLoad]);

  const handleError = useCallback((e) => {
    setError(true);
    setLoaded(false);
    onError?.(e);
  }, [onError]);

  // Preload da imagem quando deve carregar
  useEffect(() => {
    if (!shouldLoad || !src) return;

    const img = new Image();
    img.onload = handleLoad;
    img.onerror = handleError;
    img.src = src;

    return () => {
      img.onload = null;
      img.onerror = null;
    };
  }, [shouldLoad, src, handleLoad, handleError]);

  return (
    <ImageContainer 
      ref={imageRef}
      height={height}
      borderRadius={borderRadius}
      className={className}
      style={style}
      {...props}
    >
      {shouldLoad && src && (
        <Image
          src={src}
          alt={alt}
          width={width}
          height={height}
          objectFit={objectFit}
          objectPosition={objectPosition}
          loaded={loaded && !error}
          sizes={sizes}
          srcSet={srcSet}
          loading="lazy"
          decoding="async"
        />
      )}

      {placeholder && !loaded && !error && (
        <PlaceholderContainer show={true}>
          <PlaceholderIcon />
        </PlaceholderContainer>
      )}

      {error && (
        <ErrorContainer>
          <PlaceholderIcon />
          <div style={{ marginTop: '8px', fontSize: '0.8rem' }}>
            Erro ao carregar imagem
          </div>
        </ErrorContainer>
      )}
    </ImageContainer>
  );
});

OptimizedImage.displayName = 'OptimizedImage';

// Higher-Order Component para memoizaÃ§Ã£o avanÃ§ada de imagens
export const withImageOptimization = (Component) => {
  return memo(Component, (prevProps, nextProps) => {
    // ComparaÃ§Ã£o personalizada para otimizar re-renders
    return (
      prevProps.src === nextProps.src &&
      prevProps.alt === nextProps.alt &&
      prevProps.width === nextProps.width &&
      prevProps.height === nextProps.height &&
      prevProps.objectFit === nextProps.objectFit &&
      prevProps.loaded === nextProps.loaded
    );
  });
};

// Componente para galeria de imagens otimizada
export const ImageGallery = memo(({ images, cols = 3, gap = '1rem' }) => {
  const GalleryContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(${cols}, 1fr);
    gap: ${gap};
    width: 100%;

    @media (max-width: 768px) {
      grid-template-columns: repeat(2, 1fr);
    }

    @media (max-width: 480px) {
      grid-template-columns: 1fr;
    }
  `;

  return (
    <GalleryContainer>
      {images.map((image, index) => (
        <OptimizedImage
          key={image.id || index}
          src={image.src}
          alt={image.alt || `Imagem ${index + 1}`}
          height="200px"
          lazy={true}
        />
      ))}
    </GalleryContainer>
  );
});

ImageGallery.displayName = 'ImageGallery';

export default OptimizedImage;
