import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ErrorContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 2rem;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  text-align: center;
`;

const ErrorCard = styled(motion.div)`
  background: white;
  padding: 3rem;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  max-width: 500px;
  width: 100%;
`;

const ErrorIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 1.5rem;
  color: #e74c3c;
`;

const ErrorTitle = styled.h1`
  color: #2c3e50;
  margin-bottom: 1rem;
  font-size: 2rem;
  font-weight: 700;
`;

const ErrorMessage = styled.p`
  color: #7f8c8d;
  margin-bottom: 2rem;
  line-height: 1.6;
  font-size: 1.1rem;
`;

const ActionButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 10px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  margin: 0 0.5rem;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(102, 126, 234, 0.3);
  }

  &:active {
    transform: translateY(0);
  }
`;

const DetailsButton = styled(ActionButton)`
  background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  
  &:hover {
    box-shadow: 0 10px 20px rgba(245, 87, 108, 0.3);
  }
`;

const ErrorDetails = styled(motion.div)`
  background: #f8f9fa;
  border-radius: 10px;
  padding: 1.5rem;
  margin-top: 1.5rem;
  text-align: left;
  border-left: 4px solid #e74c3c;
  
  h3 {
    color: #2c3e50;
    margin-bottom: 1rem;
    font-size: 1.1rem;
  }
  
  pre {
    background: #2c3e50;
    color: #ecf0f1;
    padding: 1rem;
    border-radius: 5px;
    overflow-x: auto;
    font-size: 0.9rem;
    line-height: 1.4;
  }
`;

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      hasError: false, 
      error: null, 
      errorInfo: null,
      showDetails: false 
    };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    this.setState({
      error: error,
      errorInfo: errorInfo
    });

    // Log do erro para servi√ßos de monitoramento
    console.error('ErrorBoundary capturou um erro:', error, errorInfo);
    
    // Aqui voc√™ pode enviar o erro para um servi√ßo como Sentry
    // Sentry.captureException(error, { contexts: { react: { componentStack: errorInfo.componentStack } } });
  }

  handleReload = () => {
    window.location.reload();
  };

  handleGoHome = () => {
    window.location.href = '/';
  };

  toggleDetails = () => {
    this.setState(prevState => ({ showDetails: !prevState.showDetails }));
  };

  render() {
    if (this.state.hasError) {
      return (
        <ErrorContainer>
          <ErrorCard
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            <ErrorIcon>‚ö†Ô∏è</ErrorIcon>
            <ErrorTitle>Oops! Algo deu errado</ErrorTitle>
            <ErrorMessage>
              Desculpe, ocorreu um erro inesperado. Nossa equipe foi notificada e 
              estamos trabalhando para resolver o problema.
            </ErrorMessage>
            
            <div>
              <ActionButton onClick={this.handleReload}>
                üîÑ Recarregar P√°gina
              </ActionButton>
              <ActionButton onClick={this.handleGoHome}>
                üè† Ir para In√≠cio
              </ActionButton>
            </div>

            {process.env.NODE_ENV === 'development' && (
              <>
                <div style={{ marginTop: '1rem' }}>
                  <DetailsButton onClick={this.toggleDetails}>
                    {this.state.showDetails ? 'üîº Ocultar Detalhes' : 'üîΩ Ver Detalhes'}
                  </DetailsButton>
                </div>

                {this.state.showDetails && (
                  <ErrorDetails
                    initial={{ opacity: 0, height: 0 }}
                    animate={{ opacity: 1, height: 'auto' }}
                    transition={{ duration: 0.3 }}
                  >
                    <h3>Detalhes do Erro (Desenvolvimento):</h3>
                    <pre>
                      {this.state.error && this.state.error.toString()}
                      {this.state.errorInfo.componentStack}
                    </pre>
                  </ErrorDetails>
                )}
              </>
            )}
          </ErrorCard>
        </ErrorContainer>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
