import React from 'react';
import styled from 'styled-components';
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Card, CardGrid, CardTitle, CardText } from '../components/Card';

const posts = [
  {
    id: 1,
    titulo: 'Como rezar o Terço com devoção',
    resumo: 'Dicas práticas para rezar o terço diariamente e crescer na fé.',
    data: '10/04/2025',
    path: null
  },
  {
    id: 2,
    titulo: 'A importância da Eucaristia',
    resumo: 'Reflexão sobre o valor da Santa Missa e da comunhão.',
    data: '02/04/2025',
    path: null
  },
  {
    id: 3,
    titulo: 'O exemplo de Santa Teresinha',
    resumo: 'Como a pequena via pode transformar sua vida espiritual.',
    data: '25/03/2025',
    path: null
  },
  {
    id: 4,
    titulo: "Seu celular está destruindo sua vida",
    resumo: "Uma reflexão sincera sobre como nosso relacionamento com o celular está afetando nossa capacidade de viver o momento presente.",
    data: "15/03/2024",
    path: "/blog/celular"
  }
];

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 40px 20px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 10px;
  }
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const Grid = styled(CardGrid)`
  gap: 24px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    gap: 16px;
  }
`;

const BlogCard = styled(motion.div)`
  background: ${props => props.theme.colors.cardBackground};
  border-radius: 12px;
  padding: 24px;
  box-shadow: ${props => props.theme.shadows.light};
  border: 1px solid ${props => props.theme.colors.borderColor};
  transition: all ${props => props.theme.transitions.normal};
  position: relative;
  cursor: ${props => props.clickable ? 'pointer' : 'default'};

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: ${props => props.theme.colors.secondary};
    transform: scaleY(0);
    transform-origin: bottom;
    transition: transform ${props => props.theme.transitions.normal};
  }

  &:hover {
    box-shadow: ${props => props.theme.shadows.medium};
    transform: translateY(-2px);

    &::before {
      transform: scaleY(1);
    }
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 16px;

    &::before {
      width: 2px;
    }
  }
`;

const BlogTitle = styled(CardTitle)`
  padding-left: 16px;
  transition: color ${props => props.theme.transitions.normal};
  margin-bottom: 16px;

  ${BlogCard}:hover & {
    color: ${props => props.clickable ? props.theme.colors.secondary : 'inherit'};
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding-left: 10px;
    font-size: 1.1rem;
    margin-bottom: 12px;
  }
`;

const BlogExcerpt = styled(CardText)`
  padding-left: 16px;
  margin-bottom: 20px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding-left: 10px;
    margin-bottom: 16px;
  }
`;

const PostDate = styled.span`
  color: ${props => props.theme.colors.secondary};
  font-size: 0.95rem;
  font-weight: 500;
  padding-left: 16px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.8rem;
    padding-left: 10px;
  }
`;

const PlaceholderText = styled.p`
  color: ${props => props.theme.colors.textLight};
  font-style: italic;
  text-align: center;
  margin-top: 16px;
  font-size: 0.9rem;
  padding-left: 16px;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.8rem;
    padding-left: 10px;
  }
`;

const PostCard = ({ post }) => {
  const content = (
    <BlogCard
      clickable={!!post.path}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <BlogTitle clickable={!!post.path}>{post.titulo}</BlogTitle>
      <BlogExcerpt>{post.resumo}</BlogExcerpt>
      <PostDate>{post.data}</PostDate>
      {!post.path && (
        <PlaceholderText>
          O nosso querido católico está trabalhando nisso ainda.
        </PlaceholderText>
      )}
    </BlogCard>
  );

  if (post.path) {
    return (
      <Link to={post.path} style={{ textDecoration: 'none' }}>
        {content}
      </Link>
    );
  }

  return content;
};

const Blog = () => (
  <Container>
    <Helmet>
      <title>Blog - Apenas um Católico</title>
      <meta name="description" content="Reflexões, dicas e artigos católicos para sua vida espiritual." />
    </Helmet>
    <Title>Blog Católico</Title>
    <Grid>
      {posts.map((post) => (
        <PostCard key={post.id} post={post} />
      ))}
    </Grid>
  </Container>
);

export default Blog;
