import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { motion } from 'framer-motion';

const santos = [
  {
    id: 4,
    name: 'São Francisco de Assis',
    image: '/Fotos/São franciso.jpg',
    description: 'Padroeiro dos animais e da ecologia',
    link: '/santos/sao-francisco'
  },
  {
    id: 2,
    name: 'Santa Teresinha',
    image: '/Fotos/Santa terezinha.jpg',
    description: 'Doutora da Igreja e padroeira das missões.'
  },
  {
    id: 3,
    name: 'São Padre Pio',
    image: '/Fotos/Padre-Pio.webp',
    description: 'Padre estigmatizado e confessor.'
  },
  {
    id: 1,
    name: 'Nossa Senhora',
    image: '/Fotos/Nossa senhora.webp',
    description: 'Mãe de Jesus e nossa intercessora.'
  }
];

const PageContainer = styled.div`
  min-height: 100vh;
  padding: 40px 0;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 20px 0;
  }
`;

const PageTitle = styled.h1`
  text-align: center;
  margin: ${props => props.theme.spacing.xxl} 0;
  color: ${props => props.theme.colors.primary};
  font-size: clamp(2rem, 5vw, 3rem);
  position: relative;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.8rem;
    margin: ${props => props.theme.spacing.xl} 0;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  padding: ${props => props.theme.spacing.xxl} ${props => props.theme.spacing.lg};
  max-width: 1200px;
  margin: 0 auto;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.md};
    padding: ${props => props.theme.spacing.md};
  }
`;

const Card = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: 15px;
  overflow: hidden;
  box-shadow: ${props => props.theme.shadows.light};
  min-height: 180px;
  padding: 0;
  transition: all ${props => props.theme.transitions.normal};
  position: relative;

  ${({ isClickable }) => isClickable && `
    cursor: pointer;
    &:hover {
      transform: translateY(-5px);
      box-shadow: ${props => props.theme.shadows.heavy};

      img {
        transform: scale(1.05);
      }

      &::after {
        opacity: 1;
      }
    }
  `}

  ${({ isClickable }) => !isClickable && `
    filter: grayscale(50%);
    opacity: 0.8;
  `}

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      to bottom,
      rgba(27, 51, 88, 0) 0%,
      rgba(27, 51, 88, 0.8) 100%
    );
    opacity: 0;
    transition: opacity ${props => props.theme.transitions.normal};
  }

  a {
    text-decoration: none;
    color: inherit;
    display: block;
  }

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    width: 100%;
    min-height: auto;
    margin: 0;
    border-radius: 10px;
  }
`;

const CardImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-bottom: 3px solid ${props => props.theme.colors.secondary};
  transition: transform ${props => props.theme.transitions.normal};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    height: 200px;
  }
`;

const CardInfo = styled.div`
  padding: 24px;
  text-align: center;
  position: relative;
  z-index: 2;
  background: ${props => props.theme.colors.white};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: 16px;
  }

  h2 {
    color: ${props => props.theme.colors.primary};
    margin-bottom: 0.5rem;
    font-size: 1.5rem;
    transition: color ${props => props.theme.transitions.normal};

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 1.3rem;
      margin-bottom: 0.3rem;
    }
  }
  
  p {
    color: ${props => props.theme.colors.textLight};
    font-size: 1rem;
    transition: color ${props => props.theme.transitions.normal};

    @media (max-width: ${props => props.theme.breakpoints.mobile}) {
      font-size: 0.9rem;
    }
  }
`;

const PlaceholderText = styled.p`
  color: ${props => props.theme.colors.textLight};
  font-style: italic;
  margin-top: 1rem;
  font-size: 0.9rem;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 0.8rem;
    margin-top: 0.8rem;
  }
`;

const Santos = () => {
  return (
    <PageContainer>
      <Helmet>
        <title>Santos - Apenas um Católico</title>
        <meta name="description" content="Conheça a história dos santos católicos. Exemplos de fé, amor e devoção." />
      </Helmet>
      <PageTitle>Santos que Inspiram</PageTitle>
      <Grid>
        {santos.map((santo, idx) => (
          <Card
            key={santo.id}
            isClickable={!!santo.link}
            whileHover={santo.link ? { y: -10 } : {}}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: idx * 0.1 }}
            viewport={{ once: true }}
          >
            {santo.link ? (
              <Link to={santo.link}>
                <CardImage src={santo.image} alt={santo.name} />
                <CardInfo>
                  <h2>{santo.name}</h2>
                  <p>{santo.description}</p>
                </CardInfo>
              </Link>
            ) : (
              <>
                <CardImage src={santo.image} alt={santo.name} />
                <CardInfo>
                  <h2>{santo.name}</h2>
                  <p>{santo.description}</p>
                  <PlaceholderText>
                    O nosso querido católico está trabalhando nisso ainda.
                  </PlaceholderText>
                </CardInfo>
              </>
            )}
          </Card>
        ))}
      </Grid>
    </PageContainer>
  );
};

export default Santos;
