import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FiX, FiBookOpen, FiHeart } from 'react-icons/fi';

const Section = styled.section`
  padding: ${props => props.theme.spacing.xxl} 0;
  max-width: 1200px;
  margin: 0 auto;
  padding-left: ${props => props.theme.spacing.lg};
  padding-right: ${props => props.theme.spacing.lg};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    padding: ${props => props.theme.spacing.xl} ${props => props.theme.spacing.md};
  }
`;

const SectionTitle = styled(motion.h2)`
  text-align: center;
  margin-bottom: ${props => props.theme.spacing.xl};
  font-size: clamp(2rem, 5vw, 3rem);
  color: ${props => props.theme.colors.titleColor};
  position: relative;

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    font-size: 1.5rem;
    margin-bottom: ${props => props.theme.spacing.lg};
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background: ${props => props.theme.colors.secondary};
    border-radius: 2px;
  }
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: ${props => props.theme.spacing.xl};
  margin-top: ${props => props.theme.spacing.xl};

  @media (max-width: ${props => props.theme.breakpoints.mobile}) {
    grid-template-columns: 1fr;
    gap: ${props => props.theme.spacing.lg};
  }
`;

const FeatureCard = styled(motion.div)`
  background: ${props => props.theme.colors.cardBackground};
  padding: ${props => props.theme.spacing.xl};
  border-radius: 12px;
  text-align: center;
  box-shadow: ${props => props.theme.shadows.light};
  transition: all ${props => props.theme.transitions.normal};

  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.theme.shadows.medium};
  }

  .icon {
    font-size: 2.5rem;
    color: ${props => props.theme.colors.secondary};
    margin-bottom: ${props => props.theme.spacing.lg};
    display: flex;
    justify-content: center;
    align-items: center;
    width: 70px;
    height: 70px;
    background: ${props => props.theme.colors.secondary}15;
    border-radius: 50%;
    margin-left: auto;
    margin-right: auto;
  }

  h3 {
    color: ${props => props.theme.colors.titleColor};
    margin-bottom: ${props => props.theme.spacing.md};
    font-size: 1.25rem;
  }

  p {
    color: ${props => props.theme.colors.textLight};
    line-height: 1.6;
    font-size: 0.95rem;
  }
`;

const features = [
  {
    icon: <FiX />,
    title: 'Orações Diárias',
    description: 'Acesse orações tradicionais e contemporâneas para fortalecer sua fé diariamente.'
  },
  {
    icon: <FiBookOpen />,
    title: 'Histórias dos Santos',
    description: 'Conheça a vida e os milagres dos santos que nos inspiram a seguir Cristo.'
  },
  {
    icon: <FiHeart />,
    title: 'Reflexões Espirituais',
    description: 'Meditações e reflexões para aprofundar sua vida espiritual.'
  }
];

const ResourcesSection = () => {
  return (
    <Section>
      <SectionTitle
        initial={{ opacity: 0, y: 30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        Recursos Espirituais
      </SectionTitle>

      <FeaturesGrid>
        {features.map((feature, index) => (
          <FeatureCard
            key={index}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
            viewport={{ once: true }}
          >
            <div className="icon">{feature.icon}</div>
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </FeatureCard>
        ))}
      </FeaturesGrid>
    </Section>
  );
};

export default ResourcesSection;
