import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

export const BaseHeroSection = styled.section`
  position: relative;
  overflow: hidden;
  background-image: url('/Fotos/hero-jesus.png');
  background-size: cover;
  background-position: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(27, 51, 88, 0.7);
  }
`;

export const BaseHeroContent = styled.div`
  position: relative;
  z-index: 2;
  width: 100%;
  text-align: center;
  opacity: 0;
  animation: fadeIn 1s ease forwards;

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

export const BaseHeroText = styled.div`
  h1 {
    color: white;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
    font-weight: 700;
  }

  p {
    color: white;
    opacity: 0.9;
  }
`;

const BaseHero = ({ children }) => {
  return (
    <BaseHeroSection>
      <BaseHeroContent>
        <BaseHeroText>
          {children}
        </BaseHeroText>
      </BaseHeroContent>
    </BaseHeroSection>
  );
};

export default BaseHero;
