import React, { useState } from 'react';
import styled from 'styled-components';
import { Helmet } from 'react-helmet';
import { FiPlus, FiMinus, FiShare2 } from 'react-icons/fi';

const mockLaudes = {
  titulo: "Laudes (Oração da Manhã)",
  partes: [
    {
      titulo: "Abertura",
      texto: "V. Vinde, ó Deus, em meu auxílio.\nR. Socorrei-me sem demora.\nGlória ao Pai e ao Filho e ao Espírito Santo.\nComo era no princípio, agora e sempre. Amém. Aleluia."
    },
    {
      titulo: "Hino",
      texto: "Eis que a noite vai passando,\nA aurora já se anuncia.\nNós vos suplicamos, ó justos,\nQue nos guardeis neste dia."
    },
    {
      titulo: "Salmodia",
      texto: "Ant. 1. Sois vós, ó Senhor, o meu refúgio.\nSalmo 62(63) - A alma sedenta de Deus\nSois vós, ó Senhor, o meu Deus! Desde a aurora ansioso vos busco! A minha alma tem sede de vós..."
    },
    {
      titulo: "Leitura Breve",
      referencia: "Rm 13,11b-12",
      texto: "Já é hora de despertardes do sono! A noite já vai adiantada, o dia vem chegando. Despojemo-nos das ações das trevas e vistamos as armas da luz."
    },
    {
      titulo: "Preces",
      texto: "Irmãos e irmãs, celebrando com alegria a Cristo, nosso Salvador, aclamemos:\n R. Senhor, sois nossa vida e salvação!\n\n- Cristo, sol nascente, que iluminais todo ser humano, guiai nossos passos no caminho da paz.\n R. Senhor, sois nossa vida e salvação!"
    }
  ]
};

const Container = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 40px 16px 80px 16px;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 2rem;
`;

const LaudesTitle = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 0.5rem;
  color: ${({ theme }) => theme.colors.text};
`;

const ActionsContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const ActionButton = styled.button`
  background: transparent;
  border: 1px solid #ccc;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: ${({ theme }) => theme.colors.textLight};

  &:hover {
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
  }
`;

const TabsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  border: 1px solid #ddd;
  border-radius: 8px;
  overflow: hidden;
  margin-bottom: 2rem;
`;

const TabButton = styled.button`
  flex-grow: 1;
  padding: 1rem 0.5rem;
  font-size: 0.9rem;
  font-weight: 500;
  border: none;
  background: ${({ active, theme }) => (active ? theme.colors.primary : 'transparent')};
  color: ${({ active, theme }) => (active ? theme.colors.white : theme.colors.textLight)};
  cursor: pointer;
  transition: all 0.3s ease;
  border-bottom: 1px solid #ddd;

  &:not(:last-child) {
    border-right: 1px solid #ddd;
  }
`;

const Content = styled.div`
  font-size: ${({ fontSize }) => fontSize}px;
  line-height: 1.8;
  white-space: pre-wrap;
  color: ${({ theme }) => theme.colors.textLight};
  
  h2, h3 {
    color: ${({ theme }) => theme.colors.text};
    margin-bottom: 1rem;
    font-size: ${({ fontSize }) => fontSize * 1.1}px;
  }
`;

const Laudes = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [fontSize, setFontSize] = useState(16);
  const { titulo, partes } = mockLaudes;

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Laudes (Oração da Manhã)',
        text: `Reze as Laudes de hoje.`,
        url: window.location.href,
      }).catch((error) => console.log('Erro ao compartilhar', error));
    } else {
      alert("Compartilhamento não suportado neste navegador.");
    }
  };

  return (
    <Container>
      <Helmet>
        <title>Laudes - Apenas um Católico</title>
        <meta name="description" content="Reze as Laudes (Oração da Manhã) do Ofício Divino." />
      </Helmet>
      
      <Header>
        <LaudesTitle>{titulo}</LaudesTitle>
        <ActionsContainer>
          <ActionButton onClick={() => setFontSize(s => Math.max(s - 1, 12))} aria-label="Diminuir fonte">
            <FiMinus />
          </ActionButton>
          <ActionButton onClick={() => setFontSize(s => Math.min(s + 1, 24))} aria-label="Aumentar fonte">
            <FiPlus />
          </ActionButton>
          <ActionButton onClick={handleShare} aria-label="Compartilhar">
            <FiShare2 />
          </ActionButton>
        </ActionsContainer>
      </Header>

      <TabsContainer>
        {partes.map((parte, index) => (
          <TabButton 
            key={index} 
            active={activeTab === index} 
            onClick={() => setActiveTab(index)}
          >
            {parte.titulo}
          </TabButton>
        ))}
      </TabsContainer>

      <Content fontSize={fontSize}>
        {partes[activeTab].referencia && <h3>{partes[activeTab].referencia}</h3>}
        <p>{partes[activeTab].texto}</p>
      </Content>
    </Container>
  );
};

export default Laudes;
