import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const ImageContainer = styled.div`
  position: relative;
  display: inline-block;
  width: 100%;
  max-width: ${props => props.maxWidth || '100%'};
`;

const Image = styled.img`
  width: 100%;
  height: ${props => props.height || 'auto'};
  object-fit: ${props => props.objectFit || 'cover'};
  object-position: ${props => props.objectPosition || 'center'};
  border-radius: ${props => props.borderRadius || '0'};
  transition: opacity 0.3s ease, transform 0.3s ease;
  opacity: ${props => props.loaded ? 1 : 0};
  transform: ${props => props.loaded ? 'scale(1)' : 'scale(0.95)'};
  
  &:hover {
    transform: ${props => props.hover ? 'scale(1.05)' : 'scale(1)'};
  }
`;

const Placeholder = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, 
    ${props => props.theme.colors.borderColor} 25%, 
    transparent 25%, 
    transparent 75%, 
    ${props => props.theme.colors.borderColor} 75%
  );
  background-size: 20px 20px;
  animation: shimmer 2s infinite;
  border-radius: ${props => props.borderRadius || '0'};
  display: ${props => props.loaded ? 'none' : 'block'};

  @keyframes shimmer {
    0% { background-position: 0 0; }
    100% { background-position: 40px 40px; }
  }
`;

const OptimizedImage = ({ 
  src, 
  alt, 
  width, 
  height, 
  objectFit = 'cover',
  borderRadius = '0',
  hover = false,
  lazy = true,
  ...props 
}) => {
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(false);
  const imgRef = useRef();

  const handleLoad = () => {
    setLoaded(true);
  };

  const handleError = () => {
    setError(true);
    setLoaded(true);
  };

  // Fallback para erro de imagem
  if (error) {
    return (
      <ImageContainer maxWidth={width}>
        <div style={{
          background: '#f0f0f0',
          padding: '20px',
          textAlign: 'center',
          color: '#666',
          borderRadius: borderRadius
        }}>
          ⚠️ Imagem não encontrada
        </div>
      </ImageContainer>
    );
  }

  return (
    <ImageContainer maxWidth={width}>
      <Image
        ref={imgRef}
        src={src}
        alt={alt}
        width={width}
        height={height}
        objectFit={objectFit}
        borderRadius={borderRadius}
        hover={hover}
        loaded={loaded}
        loading={lazy ? 'lazy' : 'eager'}
        onLoad={handleLoad}
        onError={handleError}
        {...props}
      />
      <Placeholder 
        loaded={loaded} 
        borderRadius={borderRadius}
      />
    </ImageContainer>
  );
};

export default OptimizedImage;
